INSERT INTO HIST_MOVIE (ID, ID_MOVIE, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED) VALUES (17, 1021, 'Hellboy (DC)', 'Hellboy (DC)', 4, 'Akční horor, který režisér Guilermo del Toro natočil podle známého Mignolova komiksu, vypráví o Chlapci z pekla, který přišel na svět v roce 1944. Tehdy se ho snažili využít spřeženci Zla v nacistických službách - nesmrtelný Rasputin (Karel Roden), gestapačka Ilsa a maskovaný zabiják Kroenen. Malého ďáblíka však zachránil a vychoval doktor Broom (John Hurt), který pracoval pro Američany. Dospělý Hellboy (Ron Perlman) žije v přísně utajeném Úřadu pro výzkum paranormálních jevů a spolu s žabím mužem Sapienem (Doug Jones) a dívkou Liz (Selma Blairová), která umí v rozčilení vyvolat výbuch či požár, jsou využíváni při mimořádných operacích FBI. Zlo se však nevzdává. Trojice pekelných vyslanců se vrací, aby dokonala své temné dílo, a Hellboy se jim musí postavit...', 26, null, '132', '2004', 1, 1, 0);
INSERT INTO HIST_MOVIE (ID, ID_MOVIE, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED) VALUES (18, 1022, 'Hellboy (DC) dvd1', 'Hellboy (DC)', 4, 'Akční horor, který režisér Guilermo del Toro natočil podle známého Mignolova komiksu, vypráví o Chlapci z pekla, který přišel na svět v roce 1944. Tehdy se ho snažili využít spřeženci Zla v nacistických službách - nesmrtelný Rasputin (Karel Roden), gestapačka Ilsa a maskovaný zabiják Kroenen. Malého ďáblíka však zachránil a vychoval doktor Broom (John Hurt), který pracoval pro Američany. Dospělý Hellboy (Ron Perlman) žije v přísně utajeném Úřadu pro výzkum paranormálních jevů a spolu s žabím mužem Sapienem (Doug Jones) a dívkou Liz (Selma Blairová), která umí v rozčilení vyvolat výbuch či požár, jsou využíváni při mimořádných operacích FBI. Zlo se však nevzdává. Trojice pekelných vyslanců se vrací, aby dokonala své temné dílo, a Hellboy se jim musí postavit...', 26, null, '132', '2004', 1, 1, 0);
INSERT INTO HIST_MOVIE (ID, ID_MOVIE, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED) VALUES (19, 1048, 'Králova řeč', 'King''s Speech', 19, 'Po smrti otce, anglického krále Jiřího V. (Michael Gambon) a skandální abdikaci krále Eduarda VIII. (Guy Pearce), je Bertie (Colin Firth), který celý život trpí vadou řeči, nečekaně korunovaný králem Jiřím VI. Země stojí na prahu 2. světové války a zoufale potřebuje silného vůdce. Proto králova žena Alžběta (Helena Bonham Carter), budoucí královna matka, domluví svému muži setkání s excentrickým terapeutem řeči Lionelem Loguem (Geoffrey Rush). Po tuhém začátku se tito dva ponoří do neobvyklého způsobu léčby a časem mezi nimi vznikne nenarušitelné pouto. S pomocí Loguea, rodiny, vlády a Winstona Churchilla (Timothy Spall) král překoná koktání a pronese zásadní řeč v rádiu, která inspiruje anglický lid a spojí jej ve válečné bitvě.', 26, null, '118', '2011', 1, 1, 0);
