INSERT INTO HIST_MOVIE (ID, ID_MOVIE, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED) VALUES (21, 1083, 'http://www.csfd.cz/film/236467-tintinova-dobrodruzstvi/', 'Adventures of Tintin', 5, 'Příběh filmu, inspirovaného po celém světě oblíbenými příhodami knižního a comicsového hrdiny Tintina belgického autora, publikujícího pod pseudonymem Hergé, sleduje osudy výjimečně zvídavého mladého reportéra Tintina a jeho věrného psího společníka Filuty. Ti společně narazí na model lodi, který v sobě ukrývá nebezpečné tajemství. Tintin, který je nevědomky vtažen do samého středu několik set let staré záhady, se ocitá na mušce Ivana Ivanoviče Sacharina, ďábelského padoucha, který je přesvědčen, že Tintin ukradl vzácný poklad, spojovaný se zákeřným pirátem zvaným Rudý Rackham. V doprovodu svého psa Filuty, starého mořského vlka kapitána Haddocka a dvojice nešikovných detektivů Kadlece a Tkadlece procestuje Tintin křížem krážem celý svět ve snaze dostat se jako první na místo posledního odpočinku lodi Jednorožec, vraku, který by ve svém nitru mohl skrývat klíč k obrovskému bohatství... A také prastarou kletbu.', 26, null, '103', '2011', 1, 1, 0);
