INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (986, 'Rain man', 'Rain man', 7, 'Charlie Babbitt (Cruise) právě zjistil, že má autistického bratra Raymonda (Hoffman) a čeká ho fantastická jízda jeho života. Nebo okružní výlet? Všechno začne ve chvíli, kdy Raymond odmítne letět letadlem a tak nezbývá než jet autem. Poprvé totiž horkokrevného Charlieho přivede na pokraj jeho trpělivosti a pak ho kompletně vtáhne do svého egocentrického světa. Nesentimentální cesta však pro oba bratry Babbittovy znamená víc, než jen překonání vzdálenosti mezi dvěma místy. Jde o sblížení dvou nesmírně odlišných lidí v "hluboce dojímavém a silném filmu" (Joel Siegel, ABC-TV)!', 26, null, '133', '1988', 1, 1, 0, 'http://www.csfd.cz/film/5954-rain-man/', 'http://www.imdb.com/title/tt0095953/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (988, 'Rychle a zběsile 5', 'Fast Five', 4, 'Nohou zašlapávají pedál plynu do podlahy, jednou rukou řídí a v té druhé třímají zbraň nebo aspoň ukazují vztyčený prostředník. Jsou prostě rychlí a zběsilí. Už popáté. Pod vedením režiséra Justina Lina, jenž si vydobyl ostruhy na předchozích dvou dílech slavné akčně závodní série, se potkávají všichni staří známí – Paul Walker, Vin Diesel a Jordana Brewster – které tentokrát doplňuje i akční hvězda první velikosti Dwayne Johnson.
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (989, 'Zdrojový kód', 'Spource Code', 4, 'Když se kapitán Colter Stevens (Jake Gyllenhaal) probudí v sedadle příměstského vlaku, netuší, kde se tam vzal. Naproti němu sedí Christina (Michelle Monaghan). Colter ji vidí poprvé v životě, ale Christina ho očividně dobře zná. Colter Stevens zjišťuje, že se probudil v těle jiného člověka. Než se stihne z nastalé situace vzpamatovat, je vlak rozmetán silným výbuchem. V zápětí se Colter se objeví v podivném bunkru izolovaném od okolního světa. Přes obrazovku k němu mluví důstojnice Goodwin (Vera Farminga) a požaduje informace o všem, co právě viděl....', 26, null, '93', '2011', 1, 1, 0, 'http://www.csfd.cz/film/269231-zdrojovy-kod/', 'http://www.imdb.com/title/tt0945513/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (991, 'Všemocný', 'Limitless', 3, 'Eddie Morra (Bradley Cooper) je zlenivělý spisovatel, kterému se začíná hroutit svět. Chtějí ho vyhodit z práce a rozchod s přítelkyní Lindy (Abbie Cornish) ho jen utvrdí v přesvědčení, že jeho život je v troskách a budoucnost nemůže přinést nic než další zklamání. To se ale změní ve chvíli, kdy Eddieho navštíví jeho starý přítel z dětství, Vernon (Johnny Whitworth). Nabídne mu pilulky NZT - utajovanou drogu, která umožňuje využívat mozek na 100 % a dodává člověku extrémní sebevědomí. Eddie se nechává zlákat neodolatelnými účinky NZT a naprosto droze propadne. Najednou je schopen využívat informace s ohromnou efektivitou. Vše, co kdy viděl, slyšel a přečetl, zvládá zpracovat a zužitkovat.', 26, null, '105', '2011', 1, 1, 0, 'http://www.csfd.cz/film/276351-vsemocny/', 'http://www.imdb.com/title/tt1219289/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (992, 'Piráti z Karibiku: Na vlnách podivna', 'Pirates of the Caribbean: On Stranger Tides', 11, 'Johnny Depp se vrací ke své ikonické roli kapitána Jacka Sparrowa v akčním příběhu plném pravdy, zrady, mládí a odchodů. Když se Jack setká se ženou ze své minulosti (Penelope Cruz), není si jistý, zda jde o lásku - nebo zda tato žena jen „hraje na city“ a využívá ho, aby našla legendární Pramen mládí. Když ho donutí, aby vstoupil na palubu Queen Anne’s Revenge, lodi obávaného piráta Černovouse (Ian McShane), Jack se ocitá v nečekaném dobrodružství, kde neví, koho se obávat více: Černovouse nebo ženy ze své minulosti.', 26, null, '137', '2011', 1, 1, 0, 'http://www.csfd.cz/film/252716-pirati-z-karibiku-na-vlnach-podivna/', 'http://www.imdb.com/title/tt1298650/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (993, 'Thor', 'Thor', 10, 'Thorovu (Chris Hemsworth) otci, vládci bohů Odinovi (Anthony Hopkins), trvalo spoustu let, než ve své říši nastolil křehký mír. Jeho arogantní a domýšlivý syn ale dělá všechno proto, aby tuhle rovnováhu narušil. Když pohár trpělivosti jeho otce přeteče, Odin Thora exemplárně potrestá. Odebere mu jeho božské schopnosti a svrhne ho mezi obyčejné smrtelníky na planetu Zemi. Svržený a zavržený Thor lidmi zpočátku opovrhuje, postupně se mu ale začnou dostávat pod kůži, zejména sličná vědkyně Jane Foster (Natalie Portman). Thorovi ale není dopřáno užívat si pozemského klidu. Jeho bratr Loki (Tom Hiddleston) chce využít Thorova aktuálního oslabení a pošle na něj armádu všehoschopných pochopů, která v duchu přísloví „Když se kácí les, létají třísky", rozpoutá peklo na Zemi. Boží synek se bude muset rychle rozhodnout, jestli nechá lidi za sebe krvácet, nebo se vzchopí, zvedne kladivo a vyrazí na zteč.', 26, null, '115', '2011', 1, 1, 0, 'http://www.csfd.cz/film/234300-thor/', 'http://www.imdb.com/title/tt0800369/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (994, 'S.W.A.T.: Pod palbou', 'S.W.A.T.: Firefight', 4, 'Expert protiteroristické jednotky, špičkový důstojník losangeleské policie Paul Cutler (Gabriel Macht – Za nepřátelskou linií) je povolán do Detroitu, aby se tu ujal výcviku členů týmu S.W.A.T. a seznámil je s nejmodernějšími technikami záchrany rukojmích. Plánovaný průběh ale zásadně nar uší nepovedený zákrok u vyhroceného rodinného sporu, během kterého umírá rukojmí. Na scéně se objevuje posedlý vládní zabiják (Robert Patrick – Terminátor 2: Den zúčtování), který má za úkol Cutlera a ostatní členy týmu odstranit.', 26, null, '85', '2011', 1, 1, 0, 'http://www.csfd.cz/film/290936-swat-pod-palbou/', 'http://www.imdb.com/title/tt1621429/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (995, 'Drive Angry', 'Drive Andry', 4, 'Milton, otrlý zločinec, uprchl z pekla za svou poslední šancí na vykoupení. V úmyslu má zastavit zločinecký kult, který zavraždil jeho dceru. Má tři dny na to, aby jim překazil obětování miminka jeho dcery při úplňku. Připojí se k němu mladá sexy číšnice, která nabídne Miltonovi své nadupané auto po ex-příteli.Ti dva jsou na celému kultu na stopě a sami přitom mají v patách policii. Horší ale je, že Miltona sleduje také záhadný zabiják, kterého poslal sám ďábel, aby Miltona přivedl zpět do pekla. Hnán nejsilnější motivací musí překročit Milton hranice lidských možností, aby pomstil smrt své dcery dřív, než jeho poslední šance na vykoupení zmizí.', 26, null, '104', '2011', 1, 1, 0, 'http://www.csfd.cz/film/268617-drive-angry/', 'http://www.imdb.com/title/tt1502404/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (996, 'Machete', 'Machete', 4, 'Vypadal jako docela obyčejný dělník z ulice a byl ideálním kandidátem k tomu, aby na něj hodili vraždu. Ale ukázalo se, že je to Machete (Danny Trejo), legendární bývalý elitní agent, kterého se nevyplatí naštvat. Poté, co ho vyhlášený mexický drogový magnát Torrez (Steven Seagal) nechal v pustině polomrtvého, se mu podaří přežít a uprchne do Texasu, kde touží zmizet a uniknout své minulosti. Narazí tu ale na hustou síť korupce a podvodů, na jejímž konci se nachází zastřelený senátor (Robert de Niro) a Machete coby člověk, obviněný z jeho vraždy. Machete se vydává očistit své jméno a odhalit zákeřné a rozsáhlé spiknutí. V cestě mu ale stojí Booth (Jeff Fahey) – nemilosrdný podnikatel, který má na své výplatní listině slušnou zásobu zabijáků, Von (Don Johnson) – šílený strážce hranice, který velí menší armádě, a Santana Rivera (Jessica Alba) – překrásná imigrační úřednice, která neví, zda se má řídit zákonem či srdcem.', 26, null, '101', '2010', 1, 1, 0, 'http://www.csfd.cz/film/257725-machete/', 'http://www.imdb.com/title/tt0985694/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (997, 'Sucker punche', 'Sucker Punche', 10, 'Epická akční fantasy podívaná nás zavede do smyšleného světa mladé dívky, jejíž snový svět poskytuje únik z temné reality. Nelimitovaná hranicemi času a prostoru může jít kamkoli si její mysl zamane. Jenže neuvěřitelná dobrodružství jaksi smazávají hranice mezi skutečností a fantazií... s možná tragickými následky.', 26, null, '110', '2011', 1, 1, 0, 'http://www.csfd.cz/film/248845-sucker-punch/', 'http://www.imdb.com/title/tt0978764/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1000, 'Červená Karkulka', 'Red Riding Hood', 10, 'V romantickém fantasy thrilleru Červená Karkulka hraje Amanda Seyfried mladou a krásnou Valerii, která se zmítá mezi dvěma muži. Zamilovala se do hloubavého samotáře Petera (Shiloh Fernandez), avšak její rodiče dohodli sňatek se zámožným Henrym (Max Irons). Valerie a Peter nechtějí ztratit jeden druhého, proto se rozhodnou pro útěk, když se dozvědí, že Valeriina sestra byla zavražděna vlkodlakem, který loví v tmavém lese obklopující vesnici. Lidé z vesnice udržují po celá léta nelehké příměří se zlou bestií tím, že jí nabízejí každý měsíc zvířecí oběť. Pod krvavým úplňkem si  vlkodlak vybere ale oběť větší - lidský život, a tak lidé hladoví po pomstě zavolají známého lovce vlkodlaků, otce Solomona (Gary Oldman), aby jim pomohl bestii zabít. Solomon ale upozorní vesničany na to, že se vlkodlak přes den mění v člověka a že to může být kdokoliv z nich. Když počet obětí roste s každým úplňkem, začne se Valerie domnívat, že vlkodlakem by mohl být někdo blízký.', 26, null, '100', '2011', 1, 1, 0, 'http://www.csfd.cz/film/283223-cervena-karkulka/', 'http://www.imdb.com/title/tt1486185/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1001, 'Babel', 'Babel', 7, 'Babel je již třetím snímkem vzešlým z úspěšné spolupráce režiséra Alejandra Gonzáleze Iñárrita a scenáristy Guillerma Arriagy (Amores Perros, 21 gramů). Stejně jako v jejich předchozích společných projektech se i nyní náhodně proplete několik zdánlivě nesouvisejících lidských osudů. Dvojice Američanů, Richard a Susan, je na zájezdě v Maroku. Kvůli Susanině zdraví však musí svůj pobyt zde narychlo prodloužit... Chůva Amelia se bojí, že nestihne svatbu příbuzných v Mexiku. Spolu se synovcem se rozhodne, že tam tajně odjede i s opatrovanými dětmi... V Japons…', 26, null, '143', '2006', 1, 1, 0, 'http://www.csfd.cz/film/221646-babel/', 'http://www.imdb.com/title/tt0449467/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1002, 'Obřad', 'Rite', 7, 'Film Obřad je inspirovaný skutečnými událostmi, sleduje poněkud skeptického amerického studenta kněžského semináře Michaela Kovaka (Colin O’Donoghue). Ten navštěvuje školu exorcizmu ve Vatikánu. Michael se během svého pobytu v Římě seznámí s neortodoxním knězem Otcem Lucasem (Anthony Hopkins), který mu ukáže i temnou stranou jeho víry.', 26, null, '112', '2011', 1, 1, 0, 'http://www.csfd.cz/film/274486-obrad/', 'http://www.imdb.com/title/tt1161864/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1003, 'Týden bez závazků', 'Hall Pass', 2, 'Nejlepší kamarádi Rick a Fred (Owen Wilson a Jason Sudeikis) jsou oba ženatí už pěknou řádku let. Když začnou doma projevovat známky neklidu, rozhodnou se jejich ženy (Jenna Fischer a Christina Applegate) pro odvážný krok k záchraně jejich manželství: udělí manželům „týden bez závazků“ – týden naprosté svobody, kdy si mohou dělat cokoli budou chtít. Bez otázek. Nejprve se to zdá jako splněný sen, ale Rick a Fred brzy zjistí, že jejich očekávání od života singles – a jich samých – je naprosto a zcela mimo realitu.', 26, null, '105', '2011', 1, 1, 0, 'http://www.csfd.cz/film/272442-tyden-bez-zavazku/', 'http://www.imdb.com/title/tt0480687/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1004, 'Blade runner', 'Blade runner', 3, 'Počátkem 21. století dosáhla společnost Tyrell ve výzkumu robotů fáze Nexus. Její výrobky byly téměř identické s lidmi, nelišily se ani inteligencí. Lidé používali replikanty při nebezpečných výpravách nebo jako otroky při kolonizaci jiných planet. Po krvavé vzpouře replikantů v jedné z mimozemských kolonií byl robotům pod trestem smrti zakázán pobyt na Zemi. Na plnění tohoto nařízení dohlížely speciální jednotky Blade Runner, pověřené vyhledáváním a likvidací replikantů, kteří zákaz poruší.', 26, null, '117', '1982', 1, 1, 0, 'http://www.csfd.cz/film/8268-blade-runner/', 'http://www.imdb.com/title/tt0083658/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1005, 'Správci Osudu', 'Adjustment Bureau', 6, 'Věříte na náhodu? Nebo na osud? A co když je to všechno úplně jinak? Co když naše kroky řídí někdo, kdo s námi má konkrétní plány? A co se stane, když mu je, byť nevědomky, překazíme? Ambiciózní thriller Správci osudu podle povídky Philipa K. Dicka naznačuje, že svět může být místem, kde pro náhodu není místo a s osudy lidí si pohrávají pánové v klobouku. Nadějného politika Davida Norrise (Matt Damon) potkáváme v den, který mu může změnit život. Kandidát do amerického Senátu se už za chvíli dozví výsledky voleb. První prognózy nejsou moc příznivé, a tak s…', 26, null, '106', '2011', 1, 1, 0, 'http://www.csfd.cz/film/257240-spravci-osudu/', 'http://www.imdb.com/title/tt1385826/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1006, 'Dracula', 'Dracula', 8, 'Sídlo hraběte Draculy v Transylvanii sotva někdo objeví. V 15. století žil kníže Vlad, který se proslavil jako obratný diplomat, statečný bojovník proti pohanům a také jako nesmírně krutý muž se zálibou v napichování protivníků na kůl. Jeho postava prý inspirovala koncem minulého století spisovatele Brama Stokera k napsání dnes již klasického románu o upírech - Draculy. Ten je zde zosobněním zla, proti němuž se postaví skupina odvážných lidí v čele s profesorem Van Helsingem. Režisér Coppola se rozhodl jako první natočit Stokerův román v celé jeho šíři. Hrabě Dracula je v podstatě milující muž, který tragicky přišel o svou celoživotní lásku a v žalu proklel Boha za jeho krutost. Po staletích neklidného bloudění se dostává do Anglie, aby zde hledal odraz své dávné lásky.', 26, null, '130', '1992', 1, 1, 0, 'http://www.csfd.cz/film/1639-drakula/', 'http://www.imdb.com/title/tt0103874/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1007, 'Zkus mě rozesmát', 'Just Go with It', 2, 'Ve filmu Zkus mě rozesmát požádá plastický chirurg, který chodí s mnohem mladší učitelkou ze školy, svou věrnou asistentku, aby předstírala, že je jeho co nevidět bývalou manželkou, aby mu tak pomohla urovnat jednu neuváženou lež. Když se mu ale vymstí ještě několik dalších lží, vloží se do věci i její děti a všichni se společně vydávají na víkend na Havaji, který jim všem zcela změní život.', 26, null, '117', '2011', 1, 1, 0, 'http://www.csfd.cz/film/273582-zkus-me-rozesmat/', 'http://www.imdb.com/title/tt1564367/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1008, 'Magická posedlost', 'Practical Magic', 10, 'Černá komedie na motivy bestselleru Alice Hoffmanové, která se odehrávává v malém městečku v Nové Anglii. Příběh dvou sester (Bullocková, Kidmanová), které mají vrozenou schopnost čarovat a jejichž jediným problémem je, že každý jejich milostný vztah končí smrtí jejich partnerů. Tentokrát je však potřeba najít kouzlo, které udrží lásku při životě', 26, null, '100', '1998', 1, 1, 0, 'http://www.csfd.cz/film/2487-magicka-posedlost/', 'http://www.imdb.com/title/tt0120791/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1009, 'Od soumraku do úsvitu', 'From Dusk Till Dawn', 8, 'Bratři Seth a Richie Geckovi vykradli banku a prchají do Mexika. Jako rukojmí sebou mají úřednici Glorii Hillovou. Při zastávce v motelu se Glorie stává obětí sexuální zvrhlosti Richieho. Jako nové rukojmí berou rodinu pastora Jacoba Fullera. Po přejezdu mexických hranic se mají setkat s místními převaděči v baru Titty Twister. Všechno jde hladce (alespoň z pohledu Setha a Richieho), dokud se tanečnice, vyhazovači i barman nepromění v upíry, kteří mají chuť na čerstvou krev', 26, null, '108', '1996', 1, 1, 0, 'http://www.csfd.cz/film/7744-od-soumraku-do-usvitu/', 'http://www.imdb.com/title/tt0116367/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1010, 'Američan', 'American', 15, 'Nájemný vrah Jack (George Clooney) je neustále v pohybu a pořád sám. Poté, co jedna jeho zakázka ve Švédsku skončí mnohem krutěji, než předpokládal, se uchýlí na italský venkov. Schovává se ve středověkém městečku a užívá si chvíle nerušené smrtí. Zároveň tam ale přijme zakázku - má sestavit zbraň pro záhadnou ženu Mathildu (Thekla Reuten). Zatímco se Jack opájí klidem v horách Abruzzo, spřátelí se s místním knězem Benedettem (Paolo Bonacelli) a věnuje se krásné Claře (Violante Placido). Jejich přátelství pomalu přeroste do milostného vztahu, který je idy…', 26, null, '105', '2010', 1, 1, 0, 'http://www.csfd.cz/film/266940-american/', 'http://www.imdb.com/title/tt1440728/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1011, 'Grindhouse: Planeta Teror', 'Grindhouse: Planete Terror', 8, 'Po Tarantinově thrilleru Death Proof přichází druhý film z projektu GrindHouse, Rodriguezův Planet Terror. Manželé Dakota a William pracují v nemocnici jako doktoři. Jejich dnešní šichta vypadá spíš na funus. Čekárnu zaplavuje dav potrhaných lidí z města. Symptomy jsou podezřele shodné: nepřítomný pohled a odpudivé hnisající rány. Mezi zraněnými je i tanečnice Cherry, která přišla o nohu při jednom z útoků. Její parťák Wray spěchá do špitálu, aby jí kryl záda. I když bez nohy, Cherry ještě nezatančila svoje poslední číslo. Jak se vředy pokrytí spoluobčané proměňují v zuřivé agresory, Cherry a Wray vedou skupinku náhodných bojovníků do divoké noci. Budoucnost nenabízí nic než miliony nakažených a nespočet mrtvých. Jen několik (zatím) šťastných hledá poslední bezpečné útočiště na planetě Terror', 26, null, '86', '2007', 1, 1, 0, 'http://www.csfd.cz/film/221616-grindhouse-planeta-teror/', 'http://www.imdb.com/title/tt1077258/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1012, 'Hon na čarodějnice', 'Season of the Witch', 10, 'Výpravný film vypráví příběh hrdinného křižáka Behmana (Nicolas Cage) a jeho nejbližšího přítele Felsona (Ron Perlman). Po desetiletích boje se vracejí do vlasti, kterou nacházejí zdevastovanou morem. Církevní hodnostáři jsou přesvědčeni, že za katastrofu je odpovědná dívka obviněná z čarodějnictví, a proto dvojici hrdinů rozkáží, aby ji dopravili do vzdáleného kláštera. Zde se má údajná čarodějnice podrobit prastarému rituálu vymítání ďábla. Vydávají se tedy na strastiplnou cestu, která je zkouškou jejich sil a kuráže a během ní odhalí dívčino temné tajemství...', 26, null, '95', '2011', 1, 1, 0, 'http://www.csfd.cz/film/248144-hon-na-carodejnice/', 'http://www.imdb.com/title/tt0479997/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1013, 'Goemon', 'Goemon', 4, 'Japonsko na konci 16. století je zmítáno nepokoji a boji o moc v nejvyšších kruzích. Lid přestal mít důvěru k šlechticům a jejich slibům o ukončení věčných válek. Lidé jsou nadšeni jednáním ninji Goemona. Ten okrádá bohaté a dává chudým. Goemon má velmi bolestivou minulost, která se začíná čím dál více projevovat v jeho současném životě. Aniž by chtěl, zaplete se Goemon do boje o moc mezi šlechtici.', 28, null, '128', '2009', 1, 1, 0, 'http://www.csfd.cz/film/251845-goemon/', 'http://www.imdb.com/title/tt1054122/');
