INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1024, 'Letopisy Narnie: Plavba Jitřního poutníka', 'Chronicles of Narnia: Voyage of the Dawn Treader', 10, 'Edmund a Lucinka, dvě nejmladší z dětí Pevensieových, tráví prázdniny u svých příbuzných společně se svým nesnesitelným bratrancem Eustacem. Často tu vzpomínají na Narnii a na dobrodužství, které tam zažili, když jednoho dne z ničeho nic ožije obraz lodi, který visí v Lucinčině pokoji a oni se skrz něj dostávají zpátky do Narnie. Bohužel i s bratrancem Eustacem. Z moře, kam se pomocí obrazu přenesli, je zachraňuje král Kaspian desátý, kterému děti v předchozím díle, společně se sourozenci Petrem a Zuzanou, pomohly ke královskému trůnu. Nyní se mladý král na své lodi s podivným názvem Jitřní Poutník vydává splnit svou přísahu a najít sedm ztracených lordů z Narnie, kteří se kdysi dávno ztratili během nebezpečné výpravy. Pevensieovi si samozřejmě takové dobrodružství nemohou nechat ujít, jsou štastni, že jsou zpět v kouzelném království, a tak opět stanou po Kaspianově boku a plují s ním podél nebezpečných ostrovů, ale také přátelskými vodami a všude zažívají veliká dobrodužství. Na konci své cesty stanou až na samém konci světa…', 26, null, '113', '2011', 1, 1, 0, 'http://www.csfd.cz/film/243146-letopisy-narnie-plavba-jitrniho-poutnika/', 'http://www.imdb.com/title/tt0980970/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1025, 'Barbar Conan', 'Conan the barbarian', 4, 'Legendární Barbar Conan je zpátky! Z kultovní komixové postavy se Conan vypracoval na ikonu stříbrného plátna, nové nadupané zpracování navíc uvidíme v kvalitním plnohodnotném 3D. Conanova touha po pomstě za smrt jeho rodičů se záhy změní v krutý boj za záchranu národů Hyborie před mocnými silami zla, ve kterém se bitvy s nelítostnými nepřáteli proplétají se souboji s odpornými monstry. Jedinou nadějí na vysvobození Hyborie ze spárů zla je Conan a jeho síla a odvaha.', 26, null, '110', '2011', 1, 1, 0, 'http://www.csfd.cz/film/226039-barbar-conan/', 'http://www.imdb.com/title/tt0816462/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1026, 'Bod zlomu', 'Point Break', 4, 'Agent FBI Johny Utah (Keanu Reeves) sa tajne vydáva medzi kalifornských surfistov, aby vystopoval Ex-prezidentov - ozbrojenú bandu bankových lupičov. Spoznáva sa s výborným potápačom a surfistom Bodhim (Patrick Swayze), ktorého drogou je adrenalín a potešením riskovať život. Utah hľadá pravdu, Bodhi najväčšiu vlnu a obidvaja sú natoľko ponorení do vzájomného súperenia, že takmer tomu obetujú zdravý rozum, svoju hrdosť a život.', 26, null, '122', '1991', 1, 1, 0, 'http://www.csfd.cz/film/781-bod-zlomu/', 'http://www.imdb.com/title/tt0102685/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1027, 'Kung Fu Panda 2', 'Kung Fu Panda 2', 5, 'Po si plnými doušky užívá život legendárního Dračího bojovníka, který společně s pěti přáteli, Tygřicí, Jeřábem, Kudlankou, Zmijí a Opičákem, hájí rodné údolí. Všudypřítomnou pohodu ale naruší zprávy o zlosynovi, který plánuje krádež mocné zbraně, jež by mu mohla pomoci dobýt Čínu a zničit umění kung fu. Po společně s Pěti postrachy vyráží na strastiplnou pouť napříč Čínou, na jejímž konci čeká mocný nepřítel. Jak ale mohou přeborníci kung fu zničit zbraň, která je odolná vůči tomu, co ovládají nejlépe? Po musí zapátrat v minulosti a ve svých kořenech, na které úspěšně zapomněl. Jedině tak může získat schopnosti, které mu dají šanci na vítězství.', 26, null, '91', '2011', 1, 1, 0, 'http://www.csfd.cz/film/267542-kung-fu-panda-2/', 'http://www.imdb.com/title/tt1302011/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1028, 'Auta 2', 'Cars 2', 5, 'Hvězdné závodní auto Blesk McQueen a jeho přítel, jedinečný odtahový vůz Burák, se dostanou na úžasná místa, když je jejich cesta zavede za moře. Tam budou soutěžit ve vůbec prvním celosvětovém závodě Grand Prix, kde se má rozhodnout o nejrychlejším autě na světě. Ale cesta na šampionát je plná děr, objížděk a komických překvapení, když se Burák připlete do svého vlastního netušeného dobrodružství - mezinárodní špionáže. Burák, který je rozpolcený tím, že má Bleskovi McQueenovi pomáhat ve vysokém závodu a zároveň se účastnit špionážní mise s nejvyšším utajením, se na své cestě plné akcí dostane do honičky, která ho zavede do ulic Japonska a Evropy, kde ho stopují jeho přátelé a sleduje celý svět. K legraci, která se odehrává v rychlém tempu se přidává pestrá směsice nových účinkujících aut včetně tajných agentů, hrozných darebáků a mezinárodních soupeřů.', 26, null, '106', '2011', 1, 1, 0, 'http://www.csfd.cz/film/241061-auta-2/', 'http://www.imdb.com/title/tt1216475/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1029, 'Orel deváté legie', 'Eagle, The', 7, 'Dobrodružné drama z dob římského impéria se stoupající hvězdou stříbrného plátna Channingem Tatumem v hlavní roli. V roce 140 n. l. se dva muži, římský voják Marcus (Tatum) a jeho otrok Esca (Jamie Bell), vydávají na nebezpečnou cestu na sever za hranice dosud známého světa. Dvacet let před osudnou výpravou se římská Devátá legie pod vedením Flavia Aquily vydala dobývat divoký Sever. Žádný z pěti tisíc vojáků slavné legie se z válečné výpravy nevrátil. Spolu s nimi se ztratil i zlatý Orel, jejich emblém a odznak síly. Nyní se začíná proslýchat, že Orel Deváté legie byl spatřen v divošské svatyni daleko na severu. Flaviův syn Marcus neváhá a v doprovodu svého otroka Escy vyráží do rozlehlých a neprobádaných končin kaledonských hor (dnešní Skotsko) s jasným cílem: postavit se divokým severským kmenům, obnovit čest zmizelého otce a získat nazpět posvátného Orla.', 26, null, '114', '2011', 1, 1, 0, 'http://www.csfd.cz/film/256731-orel-devate-legie/', 'http://www.imdb.com/title/tt1034389/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1030, 'Odplata', 'Payback', 4, 'Není nic horšího, než když vás zradí parťák a s ním i vaše žena. A přesně tohle se stalo Porterovi. Když za ním Val přišel, že potřebuje peníze na splacení dluhu syndikátu, tak souhlasil a společně ukradli čínské mafii 140 tisíc dolarů. Peníze si měli rozdělit půl na půl, ale Valovi jeho podíl nestačil a tak Portera střelil a společně s jeho manželkou ujel. Porter přežil, ale trvalo mu pět měsíců než se dal znovu dohromady. Mezitím Val splatil dluh syndikátu a stal se z něj chráněnec svých mafiánských bossů. Porter se vrací pro své peníze, ale získat je zpátky bude problém, zvlášť pokud si o určitý podíl řekne i párek zkorumpovaných policistů.', 26, null, '100', '1999', 1, 1, 0, 'http://www.csfd.cz/film/12855-odplata/', 'http://www.imdb.com/title/tt0120784/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1031, 'Vřískot 4', 'Scream 4', 8, 'Sidney Prescott, nyní autorka nové self-help knihy, se vrací domů do Woodsboro, které je poslední zastávkou jejího turné k vydané knize. Tady se opět spojí se šerifem Deweym a Gale, kteří jsou nyní manželé a její sestřenicí Jill a tetou Kate. Naneštěstí její návrat přiláká zpátky i Ghostface a tím se Dewey, společně s Jill, jejími kamarády a celým městečkem Woodsboro dostávají do nebezpečí.', 26, null, '111', '2011', 1, 1, 0, 'http://www.csfd.cz/film/245646-vriskot-4/', 'http://www.imdb.com/title/tt1262416/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1032, 'Kmotr', 'Godfather', 15, 'Gangsterské drama Kmotr, natočené podle stejnojmenného bestselleru Maria Puza, patří mezi přelomová díla tzv. Nového Hollywoodu, a to jak komerčně, tak umělecky. Příběh newyorské mafiánské rodiny Corleonů, jíž tvrdou rukou šéfuje starý don Vito (Marlon Brando), se odehrává v letech 1945 - 55 a zaznamenává prudký rozmach organizovaného zločinu po skončení druhé světové války. V epických, rozmáchlých obrazech před divákem ožívají osudy italsko-americké rodiny, jež svůj blahobyt založila na hazardu, prostituci, vydírání a zastrašování. Tehdy mladý režisér Francis Ford Coppola, který byl poněkud překvapivě pověřen společností Paramount realizací jednoho z jejích stěžejních projektů (své nepochybně sehrálo i jeho italsko-americké zázemí), musel redukovat některé motivy rozsáhlé předlohy, ovšem podařilo se mu věrně zachovat jejího ducha - přesný popis fungování zločinecké organizace, zachycení její hierarchie, začlenění do tehdejšího společenského vývoje i často kritizované zdůrazňování rodinné soudržnosti. Pevnou dramatickou stavbu scénáře (na němž Coppola spolupracoval s autorem předlohy) podpořilo i režijní ztvárnění, jež čerpalo především z klasických vypravěčských praktik hollywoodských děl 30. let (např. Jih proti Severu), okořeněných soudobým příklonem k realismu, zejména v zobrazování násilí a sexu. Snímek získal tři Oscary - za nejlepší film, scénář podle předlohy a mužský herecký výkon v hlavní roli (Marlon Brando, který svou cenu odmítl převzít).
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1033, 'Kmotr II', 'Godfather: Part II', 15, 'Jsme větší než U. S. Steel. Al Pacino a Robert De Niro v hlavních rolích pokračování slavné mafiánské ságy, oceněné šesti Oscary. Mimořádný úspěch Kmotra (1972) ve světových kinech logicky vedl k okamžitým úvahám o natočení pokračování, k němuž koneckonců vybízel i relativně otevřený konec originálu. Coppola, který tímto snímkem získal v Hollywoodu nesmírný respekt, ovšem nejdřív svého vlivu využil k realizaci osobního projektu Rozhovor (vyznamenaného mj. Zlatou palmou na MFF v Cannes, ale komerčně neúspěšného). Scénář Kmotra II, jenž je znovu společným dílem autora původní předlohy a režiséra, rozvíjí dvě dějové linie. Další osudy nového kmotra Michaela Corleona (Al Pacino), ovládajícího své impérium z rozsáhlého sídla v nevadském Lake Tahoe. A začátky mladého Vita Corleona (Robert De Niro) v New Yorku 20. let, jež jsou obsaženy i v Puzově původní knize. Michael musí čelit nejen svým konkurentům z podsvětí, zejména Hymanu Rothovi (pozoruhodná kreace slavného hereckého pedagoga Lee Strasberga), který využívá informací zhrzeného Michaelova bratra Freda (John Cazale), ale také zvýšenému zájmu orgánů spravedlnosti, jež v průběhu druhé poloviny 50. let, kdy se Kmotr II odehrává, zintenzivnily svou kampaň proti organizovanému zločinu. Michael své obchodní zájmy uhájí, přes nedobrovolné opuštění hazardních aktivit na revoluční Kubě, ovšem cena je vysoká. Předčasně zestárlý, zahořklý kmotr je ještě nelítostnějším šéfem než jeho otec, ale rodinné vazby, tvořící páteř prvního filmu, zmizely. Michael zůstává osamocený. Bez bratra, kterého nechal zabít, i bez manželky, která od něho odešla. 
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1034, 'Kmotr III', 'Godfather: part III', 15, 'Člověk financí a politiky. Al Pacino v hlavní roli závěrečné části proslulé mafiánské trilogie. Francis Ford Coppola uzavřel svou mafiánskou trilogii snímkem Kmotr III (1990) po více než patnáctileté odmlce. Začátek jejího děje umístil do roku 1979, tedy plná dvě desetiletí od závěru Kmotra II. Michael Corleone (Al Pacino), nyní šedesátiletý, dál ovládá pevnou rukou své obchodní zájmy. Velmi usilovně se však snaží odpoutat od vlastní kriminální minulosti a své obchody legalizovat. Jeho zájem se přesunul od hazardu k nemovitostem a investičním bankám. A stal se z něho zbožný a vůči církvi nesmírně štědrý člověk, neboť přízeň Vatikánu, kde má několik vysoce postavených přátel, má v jeho proměně v bezúhonného byznysmena sehrát rozhodující úlohu. Jako svého nástupce jmenuje Vincenta Manciniho (Andy Garcia), nelegitimního potomka Sonnyho Corleona. Násilí však z vlastního života nelze jen tak jednoduše vymazat a je jen otázkou času, kdy minulost začne promlouvat do přítomnosti. Na rozdíl od prvních dvou částí vznikal Kmotr III až na několik newyorských scén prakticky celý v Itálii (v exteriérech v Římě a jeho okolí, v sicilském Palermu a Taormině a také v dekoracích postavených ve čtyřech největších ateliérech římského studia Cinecitta). Z klíčových členů štábu zůstali jen kameraman Gordon Willis, výtvarník Dean Tavoularis a samozřejmě spoluautor scénáře Mario Puzo.
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1035, 'Božská relikvie', 'Longxiong hudi', 4, 'Dobrodruh a hledač pokladů Jackie odvážně získá v jednom starobylém městě v poušti zbraň patřící k výzbroji bohů. Ovšem strážci zbraní nejsou ochotni vzdát se svého pokladu. Členové kmenu unesou Jackieho přítelkyni Loreleii, aby ho tím přinutili k vydání všech zbraní patřících k výzbroji bohů. Vysvobození dívky není snadným úkolem, jelikož na cestě jsou uložené různé nástrahy, číhají na něj odvážné amazonky a zlostný čaroděj. Jméno Jackie Chan je už dobře známo nejen mezi milovníky bojových umění, kteří se kochají pohledem na jeho bravurní kung-fung scény, ale okouzlují nás i jeho komedie. Samozřejmě i v tomto filmu se setkáme se spoustou humoru, který je pro něj tak charakteristický a na konci se zasmějeme nad kopancemi při natáčení scének.', 29, null, '88', '1986', 1, 1, 0, 'http://www.csfd.cz/film/1440-bozska-relikvie/', 'http://www.imdb.com/title/tt0091431/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1036, 'Božská relikvie 2', 'Fei ying gai wak', 4, 'Jackie je tajný agent, kterého vláda pověřila neobyčejně nebezpečným úkolem. Musí najít a získat nacistické zlato, ukryté před 40-ti lety v severní Africe. Tento úkol nebude snadný, neboť Jackie není jediný, kdo se o tento poklad zajímá. Chtějí se ho zmocnit i gangsteři, kteří jsou ochotni dopustit se čehokoliv v zájmu získání pokladu. Na plátně se opět objeví hongkongský Indiana Jones, který se jako vždy úspěšně postaví proti všem nepřátelům. Samozřejmě i v tomto filmu se setkáme s humorem charakteristickým pro Jackieho filmy a na konci se zasmějeme nad kopancemi při natáčení scének.', 29, null, '92', '1991', 1, 1, 0, 'http://www.csfd.cz/film/1433-bozska-relikvie-2/', 'http://www.imdb.com/title/tt0099558/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1037, 'Nevinnost', 'Nevinnost', 7, 'Lásky se nikdy nezbavíš.
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1038, 'Lidice', 'Lidice', 7, 'Příběh Lidic, je příběhem obyčejných lidí, kteří se absurdní shodou náhod připletli do cesty dějinám. Podle scénáře Zdeňka Mahlera, oceněného cenou Sazky v r. 2007, nabízí pohled na tragický osud Lidic za 2. světové války z neobvyklé perspektivy. Přibližuje osudy obyvatel Lidic skrz mezilidské vztahy, a to zejména lásku, která stojí na začátku celého příběhu. Jde o velkofilm plný emocí, který poukazuje na to, jak zdánlivé maličkosti mohou změnit chod dějin a způsobit tragédii. Rok 1942 – německá propaganda úzkostlivě tají genocidu páchanou na civilním obyvatelstvu. Po atentátu na druhého muže třetí říše Reinharda Heydricha však udělá výjimku. Po vypálení malé české vesnice Lidice, vše vyhlásí do světa. Jako záminka k vypálení obce stačí jeden milostný dopis. V době svého tragického konce, v červnu 1942, měly Lidice za sebou dějiny trvající nejméně šest staletí. Nacisté chladně popravili všechny lidické muže, ženy a děti nechali převézt do koncentračního tábora. Některé děti dali na převýchovu. Celková bilance – 192 popravených mužů, 58 žen zemřelo v koncentračních táborech a 88 dětí bylo posláno do plynu. Obec Lidice byla vymazána z map a srovnána se zemí. Výpravný velkofilm Lidice je natočen podle skutečné události', 27, null, '126', '2011', 1, 1, 0, 'http://www.csfd.cz/film/241720-lidice/', 'http://www.imdb.com/title/tt1754123/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1039, 'Návrat do budoucnosti', 'Back to the Future', 2, 'O tom, jak se stát strůjcem svého štěstí, vypráví jeden z nejlepších a nejoriginálnějších sci-fi filmů v historii. Christopher Lloyd sestrojil stroj na cestování časem a Michael J. Fox s jeho pomocí odcestuje do minulosti. Omylem se ale ocitne v jiné době, než chtěl a bude mít hodně práce, dát všechno do pořádku...', 26, null, '111', '1985', 1, 1, 0, 'http://www.csfd.cz/film/10128-navrat-do-budoucnosti/', 'http://www.imdb.com/title/tt0088763/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1040, 'Captain America: První Avenger', 'Captain America: The First Avenger', 4, 'Steve Rogers (Chris Evans) je idealistický mladík, který stejně jako drtivá většina jeho vrstevníků touží vstoupit do armády, aby mohl Americe pomoci zdolat nacisty, až se jeho země zapojí do Druhé světové války. Jenže nemá sebemenší šanci, protože je podměrečný a neduživý a jediným jeho předpokladem je zarputilost, s jakou se nechává opakovaně vyhazovat od odvodových komisí. Ta ve finále rozhodne o tom, že ho vyberou jako vhodného kandidáta pro unikátní vědecký experiment, který má Americe produkovat Supervojáky. Jeho premiéra se vydaří a ze Stevea se díky vědeckému týmu vedeném Dr. Erskinem (Stanley Tucci) stane dokonalá lidská bytost. Na boj s nacisty přesto nedojde, protože se zjeví mnohem nebezpečnější nepřítel. Tajná organizace HYDRA, vedená všehoschopným gaunerem Johannem Schmidtem (Hugo Weaving), totiž začala spřádat vlastní plány na ovládnutí světa. Jejich nedílnou součástí má být i tajná formule Dr. Erskina na výrobu Supervojáků. Steve Rogers dostane štít, který se stane jeho poznávacím znamením, skupinku schopných parťáků a vyrazí do mise, která mu může zajistit nesmrtelnost. Nebo přinést smrt.', 26, null, '124', '2011', 1, 1, 0, 'http://www.csfd.cz/film/227773-captain-america-prvni-avenger/', 'http://www.imdb.com/title/tt0458339/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1041, 'Transformers 3', 'Transformers: Dark of the Moon', 4, 'V originále nese třetí díl Transformers podtitul „Temná strana Měsíce“. Právě tady před lety posádka první americké měsíční expedice Apollo 11 objevila mimozemské vesmírné plavidlo a moudře si to nechala pro sebe, respektive pro tajné služby. Podle Autobotů, kteří už dvakrát dokázali ochránit Pozemšťany před ničivou invazí jejich zlých konkurentů Deceptikonů, to ale byla zásadní chyba. Chyba, která by mohla zničit celou naši civilizaci, pokud nám Autoboti znovu nepomůžou. Kromě starých známých plecháčů v čele s Optimem Primem a Bumblebeem se na scénu vrací jejich největší lidský spojenec Sam (Shia LaBeouf), životem a bitvami s roboty otřískaný mladý muž, který chce ze všeho nejvíc konečně začít žít normální život. Bohužel už ne po boku šťabajzny Mikaely (Megan Fox), která byla z důvodů, jež znají jen Megan/režisér Bay/představitelé Paramountu, vyškrtnuta ze scénáře. Sama a jeho hormony naštěstí scenárista Ehren Kruger dlouho zahálet nenechá, když mu do cesty přihraje velepohlednou Carly (modelka Rosie Huntington – Whiteley). Ta se naneštěstí stane příčinou Samova dalšího angažmá v nekonečném souboji mezi Autoboty a Deceptikony. Ti se totiž rozhodli zlikvidovat celé Chicago, v němž se Carly právě vyskytuje, a tak se Sam chtě nechtě musí do tohoto města vydat, aby se ji pokusil zachránit. A jen tak bokem i celou lidskou rasu.', 26, null, '154', '2011', 1, 1, 0, 'http://www.csfd.cz/film/261832-transformers-3/', 'http://www.imdb.com/title/tt1399103/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1042, 'Green Lantern', 'Green Lantern', 4, 'Vesmír jako takový je obrovskou záhadou, avšak po staletí tu existuje malá ale velmi mocná síla. Říkají si sbor Green Lantern a jsou to ochránci míru a spravedlnosti. Všichni válečníci jsou vázáni bratrskou přísahou kvůli udržení pořádku mezi galaxiemi, každý z nich nosí prsten, ze kterého čerpá svou super sílu. Když se však objeví nepřítel jménem Parallax s hrozbou, že zničí všechnu rovnováhu Vesmíru, leží osud a zkáza Země v rukou úplně nového člena, prvního lidského člena, který kdy byl mezi členy Green Lantern : Hala Jordana. Hal je talentovaný ale nafoukaný zkušební pilot ale má v sobě něco, co sbor očividně po celou dobu potřeboval : lidskost. To přinutí Green Lantern mít úctu k síle, která poprvé není spojena s prstenem, který dává super sílu. S pomocí kopilotky a dětské lásky Carol Ferris, může Hal rychle zvládnout svou novou moc a najít odvahu překonat strach a dokázat tak, že není pouze klíč k poražení Parallaxe. Stane se největším Green Lantern všech dob ..', 26, null, '114', '2011', 1, 1, 0, 'http://www.csfd.cz/film/256183-green-lantern/', 'http://www.imdb.com/title/tt1133985/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1043, 'Mission: Impossible II', 'Mission: Impossible II', 4, 'Zvláštní agent Ethan Hunt je pověřen novým úkolem. Právě vyvinutý preparát, který může vážně ohrozit lidstvo, se ocitl v nepravých rukou Huntova někdejšího kolegy Seana. Ten se dal na cestu zločinu a terorismu a patří k nejobávanějším. Počítačového génia Luthera Ethan ke spolupráci přemlouvat nemusí, stál mu vždycky po boku, ale potřebuje na svoji stranu získat půvabnou Nyah, povoláním mimořádně zručnou zlodějku. Ještě nedávno byla Seanovou milenkou a má tedy šanci znovu získat jeho důvěru. Tentokrát ovšem s nasazením života a za cenu milostného vztahu k Ethanovi. Sean je skutečně tvrdý a neústupný protivník. Pokračování filmu Mission: Impossible (1996).', 26, null, '123', '2000', 1, 1, 0, 'http://www.csfd.cz/film/9861-mission-impossible-ii/', 'http://www.imdb.com/title/tt0120755/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1044, 'Post Coitum', 'Post Coitum', 2, 'Po orgasmu je člověk ve zvláštním rozpoložení. Směje se tomu, pro co by před chvílí vraždil, říká věci, o kterých by normálně nemluvil... Devět hlavních postav tuto situaci v našem filmu zažije mnohokrát, podle matematického výpočtu - čtyři muži x pět žen. Přesně dvacetkrát. Vzniká sled miniepizod, které se slévají do jednoho příběhu o lásce, intrikách a sexu. Film Post Coitum je především o vztazích, ze kterých se láska nevytratila, protože tam nikdy nebyla.
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1045, 'Skyline', 'Skyline', 3, 'Los Angeles se probouzí do nového dne. Svítání však přichází o dvě hodiny dříve. Nepochází ze Slunce, ale ze zvláštních zdrojů vznášejících se nad městem… Jarrod a Elaine přijeli na víkend k přátelům Terrymu a Candice. Z okna s hrůzou sledují strašlivý výjev. Lidé se vznášejí k obloze a jsou vtahováni do vesmírných lodí, které zakrývají oblohu Los Angeles. Kamarádi se zoufale snaží utéct a zachránit. Zdá se, že vesmírní narušitelé nemají slabé místo. Lidská populace je vysávána z povrchu Země. Život, jak ho známe, nenávratně končí', 26, null, '94', '2010', 1, 1, 0, 'http://www.csfd.cz/film/272710-skyline/', 'http://www.imdb.com/title/tt1564585/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1046, 'Rango', 'Rango', 5, 'Než si začal říkat Rango a vybájil si spoustu mýtů o svých hrdinských kouscích, obýval chameleon v havajské košili prostorné terárium. Nic mu v něm nechybělo, snad kromě trochy akce. Té si začne naplno užívat, když se nešťastnou náhodou ocitne uprostřed vyprahlé pouště, kde si na něm málem pochutná hladový jestřáb. A aby napětí nebylo málo, jeho další kroky vedou do města jménem Prach, v němž visí obyvatelům kolty proklatě nízko. Ve zdejší nálevně si chameleón vymyslí jméno Rango a pár hrdinských historek, čehož vzápětí lituje. Od zoufalých domorodců totiž obratem dostane šerifskou hvězdu a hromadu úkolů, mezi nimi i nalezení zdroje pitné vody a odstranění zdejších i přespolních zabijáků. Na Rangovi teď bude, zda neuvážené hrdinské kecy přetaví v činy, nebo zda zahodí hvězdu, kolty a stetsona, navlékne havajskou košili a uteče zpátky do civilizace.
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1047, 'Rio', 'Rio', 5, 'Film Rio je příběhem vzácného papouška jménem Blu, který žije na malém městě se svoji paničkou Lindou, s níž tvoří nerozlučnou dvojku. Linda ho před léty zachránila před pašeráky, kteří ho ještě jako papouščí miminko ulovili uprostřed brazilského pralesa a odvezli do zasněžené Minessoty. Blu se kvůli tomu sice nikdy nenaučil létat, ale v bezpečí a pohodlí Lindina domova se natolik zabydlel, že pro něj není problém uvařit kávu, připravit snídani, vyčistit si zuby nebo posloužit Lindě jako její osobní budík. Když jednoho dne Linda a Blu zjistí, že Blu není posledním papouškem svého druhu na světě, jak si až doposud mysleli, neváhají a vydají se na cestu do exotické Brazílie za papoušicí Perlou, která žije v samotném srdci Rio de Janeira. Krátce po jejich setkání jsou ale Blu a Perla uneseni skupinou obchodníků se zvířaty. Za pomoci chytré a životem na ulici zocelené Perly a partičky upovídaných městských ptáků se jim sice podaří uprchnout, ale vyhráno rozhodně nemají. Dokáže Blu s pomocí nových přátel najít odvahu, aby se naučil létat, zmařil plány únoscům, kteří jsou jim stále v patách, a vrátil se zpět ke své milované paniččce Lindě?', 26, null, '96', '2011', 1, 1, 0, 'http://www.csfd.cz/film/268267-rio/', 'http://www.imdb.com/title/tt1436562/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1048, 'Králova řeč', 'King''s Speech', 19, 'Po smrti otce, anglického krále Jiřího V. (Michael Gambon) a skandální abdikaci krále Eduarda VIII. (Guy Pearce), je Bertie (Colin Firth), který celý život trpí vadou řeči, nečekaně korunovaný králem Jiřím VI. Země stojí na prahu 2. světové války a zoufale potřebuje silného vůdce. Proto králova žena Alžběta (Helena Bonham Carter), budoucí královna matka, domluví svému muži setkání s excentrickým terapeutem řeči Lionelem Loguem (Geoffrey Rush). Po tuhém začátku se tito dva ponoří do neobvyklého způsobu léčby a časem mezi nimi vznikne nenarušitelné pouto. S pomocí Loguea, rodiny, vlády a Winstona Churchilla (Timothy Spall) král překoná koktání a pronese zásadní řeč v rádiu, která inspiruje anglický lid a spojí jej ve válečné bitvě.', 30, null, '118', '2011', 1, 1, 0, 'http://www.csfd.cz/film/269587-kralova-rec/', 'http://www.imdb.com/title/tt1504320/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1049, 'Útěk ze sibiře', 'Way Back', 7, 'Drama, založené na skutečné události z roku 1940, popisuje útěk mezinárodní skupinky vězňů z ruského gulagu. Uprchlíci se vymaní ze sevření nelítostné sibiřské zimy, projdou oblastí jezera Bajkal a překročí Transsibiřskou magistrálu. Před sebou však mají ještě mnohem krutější zkoušky lidské odolnosti a vůle, poušť Gobi, Himálaje a Tibet. Zachrání se až po 4 000 mílích v Indii. Film Útěk ze Sibiře je založen na pamětech Slawomira Rawicza popisujících jeho útěk ze sibiřského gulagu. Spolu s tisíci dalšími vězni podstoupil Rawicz krutý tříměsíční transport do pracovního tábora na Sibiři.', 26, null, '133', '2010', 1, 1, 0, 'http://www.csfd.cz/film/231194-utek-ze-sibire/', 'http://www.imdb.com/title/tt1023114/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1050, 'Londýnský gangster', 'London Boulevard', 15, 'Po třech letech strávených za mřížemi, Mitchel (Colin Farrell) opouští věznici s dobrými úmysly. Ale když potká starého přítele Billyho (Ben Chaplin), gangstera, který se zrovna ohlíží po nějaké práci, Mitchel se k němu připojí, výměnou za místo k bydlení. Když se znovu zaplete se svou minulostí, je vtažen do života Charlotty (Keira Knightley), filmové hvězdy která se schovává před hordou fotografů a reportérů. Přitahován její krásou a zranitelností, rychle se ujímá role jejího ochránce. Začínají spolu plánovat nový život v Los Angeles, ale plány jim zkříží gangový boss Gant. Mitchelovi je jasné, že před Gantovým hněvem není v bezpečí nikdo z jeho blízkých a rozhoduje se vyřešit věci mezi nimi jednou provždy.', 30, null, '103', '2011', 1, 1, 0, 'http://www.csfd.cz/film/253262-londynsky-gangster/', 'http://www.imdb.com/title/tt1213648/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1051, 'Fighter', 'Fighter', 7, 'Hvězdně obsazené biografické drama The Fighter v hlavních rolích s Markem Wahlbergem a Christianem Balem vypráví skutečný příběh o nepravděpodobné cestě Mickyho Warda za boxerským titulem v kategorii velterovy váhy. V jeho rockyovské cestě jej vedl nevlastní bratr Dicky, bývalý šampión, který kdysi úspěšnou kariéru vyměnil za drogy a kriminální činnost. Touha porazit soupeře v ringu se mění i v boj za rodinu a vlastní hrdost.', 26, null, '115', '2010', 1, 1, 0, 'http://www.csfd.cz/film/230220-fighter/', 'http://www.imdb.com/title/tt0964517/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1052, 'Cesta smrti', 'Born to Raise Hell', 4, 'Agent Interpolu Samuel Axel je vyslán do východní Evropy, aby odhalil obchod se zbraněmi a získal informace o různých mafiánských organizacích. Přímo během akce je však jeho tým napaden a jeden z členů umírá Samuelovi před očima. Navzdory všem příkazům se s tím nehodlá smířit a vydá se na vlastní cestu za odplatou!', 26, null, '98', '2010', 1, 1, 0, 'http://www.csfd.cz/film/268513-cesta-smrti/', 'http://www.imdb.com/title/tt1528718/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1053, 'Duch boje', 'Chocolate', 4, 'Nový film od tvůrců "Ong Bak"! Zen, která se narodila z lásky vraha Yakuzy a jedné nebezpečné krásky, je velmi zvláštní dívkou. Žije odřezána od světa a objeví v sobě vlastnost, která v kořenech změní její život. Tato obzvláštní vlastnost ji umožňuje okamžitě dokonale napodobit jakýkoliv bojovný pohyb, který uvidí. Mezitím dívčina matka onemocní rakovinou, ale přestože jim mnoho mužů dluží, nemají peněz na její léčení. Zen se rozhodne, že pomoci nově získaných schopností sežene peníze od dlužníků a zachrání nemocnou matku. Od této chvíle ji udržuje při životě jen jediná věc - boj. Přestože se narodila jako autistka, pomocí kousku čokolády se dokáže vymanit ze svého vnitřního světa a stát se zápasnicí bojového umění, jakou ještě svět nikdy neviděl.', 31, null, '110', '2009', 1, 1, 0, 'http://www.csfd.cz/film/239565-duch-boje/', 'http://www.imdb.com/title/tt1183252/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1054, 'Pařba ve Vegas', 'Hangover', 2, 'Od režiséra filmu ROAD TRIP přichází do kin nová komedie o loučení se svobodou, které se nepředstavitelným způsobem zvrhne. Dva dny před svou svatbou odjíždí Doug (Justin Bartha) a jeho tři kamarádi (Bradley Cooper, Ed Helms a Zach Galifianakis) do Las Vegas na nezapomenutelnou party. Jenže když se ráno probudí s třeštící hlavou a kocovinou, zjistí, že si vlastně vůbec nic nepamatují. Jejich luxusní hotelový pokoj vypadá jak po výbuchu a ženicha nemůže nikdo nikde najít. Bez sebemenšího zdání co se vlastně stalo a navíc v časové tísni se musí toto trio pokusit zjitsit, krok po kroku, co všechno v noci prováděli a snad se jim podaří najít Douga a včas ho dopravit do Los Angeles, aby stihl svou vlastní svatbu. Čím víc toho ale odhalují, tím víc si uvědomují, do jakých potíží se vlastně dostali.', 26, null, '96', '2009', 1, 1, 0, 'http://www.csfd.cz/film/249773-parba-ve-vegas/', 'http://www.imdb.com/title/tt1119646/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1055, 'Pařba v Bangkoku', 'Hangover Part II', 2, 'Ve filmu „Pařba v Bangkoku“ jedou Phil (Bradley Cooper), Stu (Ed Helms), Alan (Zach Galifianakis) a Doug (Justin Bartha) do exotického Thajska na Stuovu svatbu. Po nezapomenutelné rozlučce v Las Vegas nenechá Stu nic náhodě a naplánuje zcela bezpečnou, poklidnou pozdní snídani. Jenže ne všechno se vždy děje tak, jak se naplánuje. Co se stane ve Vegas, sice může zůstat ve Vegas, ale co se stane v Bangkoku, si nedokážete ani představit.', 26, null, '102', '2011', 1, 1, 0, 'http://www.csfd.cz/film/263121-parba-v-bangkoku/', 'http://www.imdb.com/title/tt1411697/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1056, 'Kamarád taky rád', 'Friends with Benefits', 2, 'Dylan (Justin Timberlake) a Jamie (Mila Kunis) rozhodně nemají na usedlý život ani pomyšlení. Když si Newyorčanka Jamie, zaměstnaná jako hledačka nových příležitostí, vyzkouší své umění na uměleckém řediteli Dylanovi z Los Angeles, kterého přemluví, aby vzal svou vysněnou práci v New Yorku, uvědomí si oba, že jsou si velice podobní. Každý z nich má za sebou tolik zkrachovalých vztahů, že jsou ochotni zkrátka pustit lásku z hlavy a starat se jen o zábavu.
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1057, 'Ošetřovatel', 'Zookeeper', 2, 'Zvířata ve Franklin Park Zoo zbožňují svého laskavého ošetřovatele Griffina Keyese (Kevin James). Griffin, kterému je příjemněji ve společnosti lva než ve společnosti ženy, usoudí, že jedinou jeho možností, jak najít dívku svých snů, je odejít ze zoo a najít si zajímavější práci. Zvířata se v záchvatu paniky rozhodnou porušit svá prastará pravidla mlčení a odhalí své největší tajemství: umí mluvit! Aby Griffina přiměla zůstat, rozhodnou se mu udělit lekce seznamování a dvoření – ve zvířecím stylu.', 26, null, '104', '2011', 1, 1, 0, 'http://www.csfd.cz/film/269228-osetrovatel/', 'http://www.imdb.com/title/tt1222817/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1058, 'Šmoulové', 'Smurfs', 2, 'Rodinná komedie, ve které se pojí animovaný i hraný film, uvádí na plátna kin všem dobře známé modré hrdiny, Šmouly. Zlý čaroděj Gargamel vyžene Šmouly z jejich vesničky a během následného pronásledování všichni společně projdou kouzelným portálem, který je zavede do našeho světa, přímo doprostřed newyorského Central Parku. Malí modří Šmoulové musejí dát hlavy dohromady a najít způsob, jak se dostat zpátky domů do vesničky dříve, než je Gargamel znovu vystopuje.', 26, null, '103', '2011', 1, 1, 0, 'http://www.csfd.cz/film/245686-smoulove/', 'http://www.imdb.com/title/tt0472181/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1059, 'Zrození planety opic', 'Rise of the Planet of the Apes', 4, 'Pořekadlo, že cesta do pekla bývá dlážděna dobrými úmysly, platí v tomto příběhu přímo ukázkově. Mladý vědec Will Rodman (James Franco) vyvíjí lék proti Alzheimerově nemoci, kterou trpí i jeho otec (John Lithgow). Slibný výzkum se ovšem zadrhává kvůli nežádoucím vedlejším účinkům, které vykazují pokusní živočichové po aplikaci léčebné látky. Zklamaný vědec opouští výzkum, odchází pečovat o otce a jako bonus si tajně odnáší šimpanzí mládě, jehož rodiče zemřeli při Willových pokusech. U šimpanze Caesara se postupně začne projevovat vysoká inteligence a Will zjistí, že za to zřejmě může působení jeho léku. Začne mu ho tajně podávat a tím nechtěně otevře bránu k lidské zkáze. Čím dál inteligentnější zvíře se po čase ocitne v přírodní rezervaci, kde začne v čele ostatních opic pomaloučku připravovat vzpouru proti těm, kdo zabili jeho rodiče a jeho zavřeli do tohoto „fešáckého kriminálu“. Tedy proti lidem.', 26, null, '105', '2011', 1, 1, 0, 'http://www.csfd.cz/film/281606-zrozeni-planety-opic/', 'http://www.imdb.com/title/tt1318514/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1060, 'Bitva o planetu Terra 3D', 'Terra', 5, 'Kouzlo Avataru a akce Hvězdných válek. Animovaný sci-fi film Bitva o planetu Terra vyšel celé dva roky před celosvětovým hitem Avatar, přitom mu může směle konkurovat i dnes! Originální příběh o planetě, kterou chce lidstvo kolonizovat, ale místní druh se tomu brání, je doplněný o hravou animaci plnou fantastických nápadů a navíc ho doprovází dokonalá hudba. Nenechte si ujít opravdovou událost ve světě sci-fi a nechte se okouzlit úžasným světem planety Terra!', 26, null, '85', '2007', 1, 1, 0, 'http://www.csfd.cz/film/235404-bitva-o-planetu-terra-3d/', 'http://www.imdb.com/title/tt0858486/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1061, 'Kovbojové a vetřelci', 'Cowboys & Aliens', 4, 'Cizinec (Daniel Craig), který se probral z bezvědomí poblíž města Absolution, si naprosto vůbec nic nepamatuje. Jediným vodítkem k jeho minulosti je podivný náramek na zápěstí. Celková amnézie je mrzutá věc, obzvlášť když se ocitnete ve městě, které nenávidí lidi odjinud a kde vládne tvrdou rukou nesmlouvavý plukovník Dolarhyde (Harrison Ford), jenž ho řídí metodou pěsti a biče. Absolution žije ve strachu a brzy bude ještě hůř. Shůry přicházejí podivné zvuky a oslepující světla, obyvatelé města beze stopy mizí a nikdo neví, jak se bránit a kdy přijde řada na něj – tomu se říká permanentní teror. Paradoxně právě cizinec, kterého pronásledovali od chvíle, kdy se v jejich městě objevil, se stane jejich jedinou nadějí. Postupně se mu vrací paměť, a jak si vzpomíná, kde byl a co tam prožil, uvědomuje si, že drží v rukou klíč k tajemství, které zdejším kovbojům dává aspoň nějakou naději v boji s  technologicky mnohem vyspělejším protivníkem. Otázkou je, jestli mu chronicky nedůvěřiví obyvatelé uvěří a jestli mu Dolarhyde dobrovolně přenechá otěže vlády nad městem.', 26, null, '119', '2011', 1, 1, 0, 'http://www.csfd.cz/film/261881-kovbojove-a-vetrelci/', 'http://www.imdb.com/title/tt0409847/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1062, 'Zkažená úča', 'Bad Teacher', 2, 'Elizabeth Halseyová (Cameron Diaz) je učitelka, která by z chování dostala přinejmenším dvojku. Je sprostá, zlá a chová se naprosto nevhodně. Pije, bere drogy a nemůže se dočkat, až si najde dobře situovaného muže, který se o ni postará a ona bude moci svou kariéru učitelky definitivně opustit. Když se s ní rozejde její snoubenec, vymyslí plán, jak získat jeho bohatého a pohledného náhradníka (Justin Timberlake) – o jehož přízeň soupeří se svou přehnaně aktivní kolegyní Amy (Lucy Punch) – ale zjišťuje, že je místo toho nucena bránit se snahám obhroublého učitele tělocviku (Jason Segel). Elizabethiny bizarní plány a jejich bláznivé následky šokují její studenty, spolupracovníky a dokonce i ji samotnou.', 26, null, '92', '2011', 1, 1, 0, 'http://www.csfd.cz/film/270857-zkazena-uca/', 'http://www.imdb.com/title/tt1284575/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1063, 'Total Recall', 'Total Recall', 4, 'Hlavním hrdinou příběhu, odehrávajícího se v roce 2084, je stavební dělník Douglas Quaid, kterého sužují děsivé noční můry, týkající se Marsu. Rozhodne se proto navštívit neobvyklou cestovní kancelář Total Recall, která nabízí dovolenou formou implantovaných vzpomínek. Quaid si vybere dovolenou na Marsu v podobě tajného agenta. Při implantaci vyjde najevo, že Quaid už jedny implantované vzpomínky má. Vzápětí se ho pokusí zavraždit dva lidé, které považoval za své blízké. Nejprve přítel a vzápětí i jeho manželka Lori. Brzy poté se Quaid dovídá, že byl na Marsu zapleten do odboje proti tyranskému guvernérovi Cohaagenovi a proto se, pronásledován najatými zabijáky, na Mars vrátí, aby našel sám sebe a své vlastní vzpomínky. Okamžitě se ocitá ve víru nebezpečných událostí a stává se rozhodujícím prvkem na misce vah vleklé války mezi rebely a guvernérovou armádou. Americký snímek natočil v roce 1990 režisér Paul Verhoeven (RoboCop, Hvězdná pěchota, Základní instinkt). Autoři scénáře, Ron Shusett a Dan O''Bannon (proslavili se jako scénáristé Vetřelce), se nechali při vzniku příběhu inspirovat povídkou Philipa K. Dicka We Can Remember It for You Wholesale. Film získal Oscara za speciální efekty a je dnes považován za žánrovou klasiku devadesátých let 20. století.', 26, null, '113', '1990', 1, 1, 0, 'http://www.csfd.cz/film/9303-total-recall/', 'http://www.imdb.com/title/tt0100802/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1064, 'Cesta samuraje', 'Warrior''s Way', 4, 'Samuraj Yang je v Číně legendou, válečníkem zrozeným na bitevním poli. Má za úkol vyvraždit poslední členy nepřátelského klanu. Úkol splní až na malou drobnost. Ponechá naživu malé dítě. Spolu s ním utíká z Číny, aby našel útočiště kdesi na Divokém západě. Rozzuření spolubojovníci jsou mu v patách. Yang nachází nový domov v malém westernovém městečku. V drsném prostředí ostrých pistolníků a jiných vyvržených existencí potkává místního opilce Ronalda (Geoffrey Rush) a tajemnou vrhačku nožů Lynne (Kate Bosworth). Podivná trojice se musí postavit nelítostnému střetu dvou světů a ochránit život dítěte…', 26, null, '100', '2010', 1, 1, 0, 'http://www.csfd.cz/film/239613-cesta-samuraje/', 'http://www.imdb.com/title/tt1032751/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1065, 'Kračející skála', 'Walking Tall', 4, 'Bývalý člen zvláštních jednotek americké armády Chris Vaughn (The Rock) se vrací do rodného města. Neplánuje nic víc než klidný život v místech, kde vyrůstal, pomoc stárnoucímu otci a obnovení dávných vztahů, které - jak doufá - svým odchodem do velkého světa úplně nezpřetrhal. Kdysi tak idylické venkovské městečko však nachází drasticky změněné: v ulicích vládne násilí a drogy. Chris brzy zjišťuje, že ohniskem nákazy je místní casino, jehož nečestný šéf a jeho pohůnci městečko korumpují. Jednou z jejich obětí je i Chrisova bývalá přítelkyně, která v casinu pracuje jako tanečnice. S roztrpčeným a pomstychtivým rodákem se do městečka vrací zákon. Na to, aby dal Chris věci znovu do pořádku, bude - spíš než znalosti paragrafů potřebovat svůj tvrdý vojenský výcvik a partu odhodlaných spřízněných duší.
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1066, 'Super 8', 'Super 8', 3, 'Když se skupinka teenagerů rozhodne na kameru „Super osmičku“ natočit zombie horor, ani ve snu by nikoho z nich nenapadlo, že výsledný film bude ještě děsivější, než zamýšleli. Jeden z nejoriginálnějších tvůrců současnosti J. J. Abrams spojil síly s největším hollywoodským expertem na mimozemšťany Stevenem Spielbergem a společně natočili film, při jehož sledování si nutně vzpomenete na E. T. Mimozemšťana nebo na Blízká setkání třetího druhu.
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1067, 'Čelisti', 'Jaws', 6, 'Písečné pláže, modré moře a zářivé nebe vítají návštěvníky rekreačního města Amity. Nikdo netuší, že pod hladinou někde čeká obrovský bílý žralok. Brzy si nalezne první oběť, posléze další a další. Zástupci města se zprvu vše snaží utajit, nakonec s napětím čekají, až se objeví někdo, kdo obrovského žraloka zabije. Na moře vyplouvají tři muži, ichtyolog Hooper, lovec žraloků Quint a šéf místní policie Brody, aby zkusili své štěstí... 
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1068, 'Šéfové na zabití', 'Horrible Bosses', 2, 'Jediná věc, která by udělala Nickovi, Kurtovi a Daleovi denní dřinu v práci snesitelnou, by byla rozdrtit jejich netolerantní šéfy na prach. Podat výpověď je bohužel nemožné, takže s podporou více než malého množství drinků a pár pochybných rad od bývalého trestance si tři kamarádi vymyslí komplikovaný a zdánlivě „blbuvzdorný“ plán, jak se navždy zbavit svých nadřízených. Je tu však jeden problém. I ten nejlepší plán je vždy jen tak spolehlivý jako mozek, který ho vymyslel ...', 26, null, '98', '2011', 1, 1, 0, 'http://www.csfd.cz/film/278538-sefove-na-zabiti/', 'http://www.imdb.com/title/tt1499658/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1069, 'Hranaři', 'Hramaři', 15, 'Hranař je člověk pohybující se na hraně zákona. Hranař může být osoba kladná i záporná. Hranař může být přemožen pouze jiným Hranařem... Thriller HRANAŘI se odehrává v české současnosti, kterou pokrývá dokonalá pavučina vzájemně propojených kšeftů a kšeftíků. Mnohem jemnější a nenápadnější, než byla ta v éře Mrázků a Krejčířů. Samozřejmý systém vratek, resp. skrytých provizi, automaticky funguje na všech úrovních. Kdo v něm nejede, je mimo hru. Čím větší hra, tím ostřejší jízda. Je to balanc na hraně útesu. Dá se na ní udržet? Jak dlouho ještě? A kdo to všechno vlastně řídí? Filmové drama se rozehraje kolem jedné velké stavební zakázky, z níž měly podle zaběhnutých pravidel putovat vratky na zaběhnutá vyšší místa. Jeden z hráčů se ovšem rozhodne pro sólový postup, načež se prudce roztočí kolotoč sledovaček, kompromitujících materiálů, tajných nahrávek, mediálních intrik, výhrůžek, vydírání a zabíjení. Systém se zadrhne a hrozí kolaps. Protihráči volí krajní řešeni...', 27, null, '96', '2011', 1, 1, 0, 'http://www.csfd.cz/film/298699-hranari/', 'http://www.imdb.com/title/tt2080325/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1070, 'Horizont události', 'Event Horizon', 3, 'V roce 2040 se vydala moderní kosmická loď Horizont události prozkoumat hranice naší sluneční soustavy. Za planetou Neptun zmizela beze stopy. Po sedmi letech však opět vydala signál ze kterého se podařilo vyluštit dvě slova, která zněla jako "spaste mě...", nebo jako "spaste se...". Na průzkumně - záchranou misi se vydává kosmická loď Louis and Clarke vedená kapitánem Millerem (Laurence Fishburne). Na palubu je přibrán i konstruktér Horizontu události dr. Weir (Sam Neill), který však posádce nepodává moc přesvědčivé vysvětlení o původu a stavbě lodi. Po příletu k lodi se začnou dít podivné věci z nichž první je, že při ofocení lodi bioscanerem se celá loď ukáže jako biologicky aktivní. Brzy posádku čeká hrůzné zjištění, kde loď celých sedm let pobývala. Tam, kde končí lidský rozum a začínají už jenom paranoidní dohady...
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1071, 'ajemná záře nad Pacifikem', 'Final Countdown', 3, 'Výročie veľkej americkej hanby a historickej traumy stále zanecháva živé stopy. Útok na prístav Pearl Harbor bol dovtedy nepredstaviteľným. Teraz, po takmer štyridsiatich rokoch sa americká lietadlová loď na čele s kapitánom Matthewom Yellandom (Kirk Douglas) plaví vodami na miestach, kadiaľ prechádzala japonská flotila pri útoku na americkú základňu. Nikto nevie, ako sa to stalo, ale z radarov loď kapitána Yellanda zmizla. O malú chvíľu preletia nad loďou japonské lietadlá. Skvelé napobeniny, hovorí posádka, lenže lietadlá útočia. Znie to neuveriteľne, ale loď prešla časovou bránou do iného kontinua a pred ňou sa nachádza japonská flotila s cieľom zaútočiť na Pearl Harbor. Píše sa rok 1941 a kapitán Matthew Yelland má možnosť zmeniť beh dejín...', 26, null, '103', '1980', 1, 1, 0, 'http://www.csfd.cz/film/8897-tajemna-zare-nad-pacifikem/', 'http://www.imdb.com/title/tt0080736/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1072, 'Muži v naději', 'Muži v naději', 2, 'Může být nevěra základem šťastného manželství? Šarmantní bonviván Rudolf je o tom přesvědčen: „Ženská má mít pocit, že o chlapa musí bojovat, musí se snažit, aby si ho udržela. A hlavně, ženská se nesmí nudit!”
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1073, 'Mortal Kombat', 'Mortal Kombat', 10, 'Mortal Kombat je jedna z nejpopulárnějších herních sérií historie. V roce 1995 se dočkala také osobité filmové verze. Její tvůrci se snaží maximálně zachovat styl počítačové hry, a tak je vlastně celý příběh pouze řadou atraktivních soubojů různých protivníků. Na počátku démonický zloduch a polykač duší Shang Tsung různými úskoky přiláká na palubu starobylé plachetnice skupinu bojovníků - mnicha Liu Kanga, který chce pomstít smrt svého mladšího bratra, herce Johnnyho Cage, kterého už nebaví hrát v hloupých filmech, policistku Sonju Bladeovou, která pronásleduje nebezpečného zločince. Loď trojici odveze do jiné dimenze, kde se má na tropickém ostrově celý turnaj konat. Jejich rádcem a duchovním vůdcem se stane mystický Lord Rayden, bůh hromu a blesku. Ten hrdinům odhalí, že jejich vítězství či prohra rozhodne o příští budoucnosti světa, protože nejde o obyčejný turnaj, ale o mystické střetnutí dobra a zla', 26, null, '101', '1995', 1, 1, 0, 'http://www.csfd.cz/film/150-mortal-kombat/', 'http://www.imdb.com/title/tt0113855/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1074, 'Mortal Kombat 2: Vyhlazení', 'Mortal Kombat: Annihilation', 10, 'Mortal Kombat 2 je novým dobrodružstvím, které začíná tam, kde předchozí film skončil. Odvážní bojovníci musejí opět čelit protivníkům ve službách nelidského Shao Khana, vládce Vnějšího světa, který prorazil vesmírný tunel mezi Vnějším světem a Zemí, aby ji ovládl. Jeho Armádu temnoty vedou nemilosrdní bojovníci s nadpřirozenými schopnostmi - Sindel, kdysi krásná královna, nyní hrozné ztělesnění smrti, obrovský kentaur Motaro a ninja Ermac, mistr telekinetických sil.', 26, null, '91', '1997', 1, 1, 0, 'http://www.csfd.cz/film/33193-mortal-kombat-2-vyhlazeni/', 'http://www.imdb.com/title/tt0119707/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1075, 'Dívka na koštěti', 'Dívka na koštěti', 2, 'Václav Vorlíček, Miloš Macourek a Hermína Franková jsou autory komedie Dívka na koštěti o mladé čarodějnici, která dala přednost životu mezi lidmi před výsadami nadpřirozených bytostí. Příběh o Saxaně, jak se začínající čarodějnice jmenuje, není jen návratem do světa pohádek, dětských snů a fantazie, ale především konfrontací se světem konkrétním a reálným. Saxana, která uteče z pohádkové říše a seznámí se s dobrem i zlem skutečného lidského světa, se nakonec přece jen rozhodne mezi lidmi zůstat, protože zde našla něco víc, než jí mohly poskytnout čarodějnické síly, totiž opravdový lidský cit...', 27, null, '76', '1971', 1, 1, 0, 'http://www.csfd.cz/film/9409-divka-na-kosteti/', 'http://www.imdb.com/title/tt0067464/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1076, 'Saxána a Lexikon kouzel', 'Saxána a Lexikon kouzel', 2, 'Tři sta let po škole! Tak zněl ortel vynesený před mnoha a mnoha lety nad Saxánou, studentkou čarodějnické školy v Říši pohádek, která utekla do světa lidí za svou první láskou Honzou Bláhou. Saxána nyní žije normálním životem v krásné rodinné vile se svým manželem Janem, dcerkou Saxánkou, bláznivou tetou Irmou a strýcem Evženem. Na co ale úplně zapomněla, je půda skrývající tajemství o jejím předešlém čarodějnickém životě. Právě toto maminčino tajemství náhodou objeví dcerka Saxánka, která se nenadálou souhrou okolností a své zvědavosti ocitne v kouzelné Říši pohádek plné trpaslíků, rarachů, draků, bazilišků, šotků a morkolabů. Na malou holčičku čekají neuvěřitelná dobrodružství neznámého světa, který se právě ocitl na pokraji zkázy. Odvážná Saxánka za pomoci svých nových pohádkových kamarádů rozplétá zamotaný svět pohádek, který se zlými silami snaží ovládnout komiksový gangster Crashman. Žákyni 2.B čeká nelehký úkol, jak se vypořádat se všemi nástrahami podzemní říše, přelstít komiksového gangstera a dostat se zpátky domů k mamince.', 27, null, '90', '2011', 1, 1, 0, 'http://www.csfd.cz/film/224830-saxana-a-lexikon-kouzel/', 'http://www.imdb.com/title/tt0969332/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1077, 'Twilight sága: Rozbřesk - 1. část', 'Twilight Saga: Breaking Dawn: Part One', 10, 'V první části závěrečného dílu strhující upíří romance s názvem Twilight sága: Rozbřesk se Bella Swan (Kristen Stewart) a Edward Cullen (Robert Pattinson) po svatbě vydávají na líbánky do Ria de Janeira, kde se konečně, stranou všeho a všech, mohou naplno oddat své lásce. Jejich štěstí, ale netrvá dlouho. Bella zmítaná prudkou vášní k upírovi Edwardovi, ale zároveň upřímnou náklonností k vlkodlakovi Jacobu Blackovi (Taylor Lautner), brzy zjišťuje, že čeká dítě. Těhotenství s upírem pro ni však znamená fatální hrozbu. Čeká ji tedy bouřlivý rok plný lásky, pokušení i ztrát. Musí se s konečnou platností rozhodnout, zda vstoupí do temného, ale lákavého světa nesmrtelných, nebo zda bude žít plnohodnotným lidským životem. Nevyhnutelnost tohoto rozhodnutí se vine celým Belliným příběhem jako červená nit, na níž visí osudy obou kmenů – upírů i vlkodlaků. A jakmile se Bella rozhodne, dojde k celému řetězu nečekaných událostí, jež mohou mít nedozírné následky.', 26, null, '112', '2012', 1, 1, 0, 'http://www.csfd.cz/film/267434-twilight-saga-rozbresk-1-cast', 'http://www.imdb.com/title/tt1324999/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1078, 'Twilight sága: Rozbřesk - 2. část', 'Twilight Saga: Breaking Dawn: Part Two', 10, 'Závěrečná část světoznámé upírské ságy nás naposledy zavede do světa, kde upíři a vlkodlaci bojují nejen o svou lásku, ale i o vlastní životy. Novorozená upírka Bella a její manžel Edward mohou být konečně navždy spolu a šťastní, ale příchod jejich pozoruhodné dcery Renesmee uvede do pohybu nebezpečný řetězec událostí, které pro ně mohou skončit katastrofou. Rodina Cullenových se ale rozhodne bojovat a kontaktuje přátele z celého světa, aby spojili své síly a postavili se vládnoucímu rodu Volturiových. Velkolepá bitva, kde vítězství může vše změnit, právě začíná. Napínavé a romantické pokračování filmu Rozbřesk vám připomene proč se Twilight sága stala fenoménem a ukradla srdce dívkám a ženám po celém světě.', 26, null, '115', '2012', 1, 1, 0, 'http://www.csfd.cz/film/281757-twilight-saga-rozbresk-2-cast/', 'http://www.imdb.com/title/tt1673434/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1079, 'Johnny English', 'Johnny English', 2, 'Britská výzvědná služba, konkrétně její oddělení MI 7, získala v agentovi Johnny Englishovi (Rowan Atkinson) skutečně jedinečnou posilu. Snad ani James Bond, připravený vždycky a všude na všechno, se s ním měřit nemůže. Právě včas, protože z londýnského Toweru byly ukradeny korunovační klenoty a Anglie čelí vážné krizi. Johnny, spolu se svým oddaným a všeho schopným asistentem Boughtem, se ihned ujímá vyšetřování a pátrání, která vede vskutku neotřelými a svéráznými způsoby. Podezřelým č. 1 se stává francouzský obchodní magnát Pascal Sauvage (John Malkovich). Nejcennější symbol moci ostrovního království nemá obohatit jeho sbírku, ale přímo jeho, dosud nekorunovanou hlavu. Budoucí král má ovšem ještě děsivější plán. Chce tu nádhernou, laskavou, zelenou a přívětivou zemi změnit v největší vězení na světě. Jaké štěstí, že Johnnymu ve chvíli, kdy už je téměř vše ztraceno, přispěchá na pomoc krásná agentka Interpolu. Alžběta se může klidně vrátit na trůn a panovat dál a Johnny? To se ještě uvidí
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1080, 'Johnny English se vrací', 'Johnny English Reborn', 2, 'Kdo zachrání Její veličenstvo britskou královnu, když agent 007 James Bond zrovna leží u ledu? Jeden muž už před lety dokázal, že je na něj spolehnutí a že raději Britské ostrovy zbourá, než by je vydal nepříteli všanc. Jmenuje se Johnny English, nosí tvář Rowana Atkinsona, alias Mr. Beana, disponuje mimořádným rejstříkem grimas a silným magnetem na katastrofy všeho druhu.
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1081, 'Rebelka', 'Brave', 5, 'Už od starých časů se po mnoho generací šíří příběhy o epických bitvách a mystických legendách v drsné a tajemné Skotské vysočině. Ve filmu se k legendě přidá nová pověst, když odvážná Merida stane tváří v tvář tradici, osudu a nejkrutější bestii. Merida je zkušená lučištnice a impulzivní dcera krále Ferguse a královny Elinor. Je odhodlána jít si v životě svou vlastní cestou. Vzepře se starému zvyku zasvěcení pánům země. Silnému lordu MacGuffinovi, mrzutému Macintoshovi a svárlivému Dingwallovi. Meridiny činy nechtěně rozpoutají v království chaos a běsnění. Když se obrátí o pomoc k podivínské staré čarodějnici, dostane prokleté přání. Merida, která následuje nebezpečné síly, objeví význam skutečné odvahy, aby zlomila příšernou kletbu, než bude příliš pozdě.', 26, null, '90', '2012', 1, 1, 0, 'http://www.csfd.cz/film/241060-rebelka/', 'http://www.imdb.com/title/tt1217209/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1082, 'Arthur a souboj dvou světů', 'Arthur et la guerre des deux mondes', 5, 'Pomocí lsti se Maltazard dostal do světa lidí. Má jasný cíl: sestavit armádu obřích seidů a převzít nadvládu nad celým světem. Vypadá to, že plány mu může zmařit jenom Arthur, který se ale musí nejdříve dostat do své ložnice a vrátit se do své původní velikosti! Uvězněný v drobném tělíčku Minimoje, se může samozřejmě spolehnout na pomoc Selenie a Betamechea a kupodivu také na Darkose, syna Maltazarda, který tvrdí, že chce přejít na jeho stranu. V závěrečném boji proti Maltazardovi, kdy jde Arthur se svými kamarády pěšky, jede na kole, v autě a dokonce i na motorce Harley Davidson, bude Arthur s kamarády potřebovat veškerou pomoc, která seženou. Bitva může začít!', 32, null, '101', '2010', 1, 1, 0, 'http://www.csfd.cz/film/231234-arthur-a-souboj-dvou-svetu/', 'http://www.imdb.com/title/tt0940656/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1083, 'Tintinova dobrodružství', 'Adventures of Tintin', 5, 'Příběh filmu, inspirovaného po celém světě oblíbenými příhodami knižního a comicsového hrdiny Tintina belgického autora, publikujícího pod pseudonymem Hergé, sleduje osudy výjimečně zvídavého mladého reportéra Tintina a jeho věrného psího společníka Filuty. Ti společně narazí na model lodi, který v sobě ukrývá nebezpečné tajemství. Tintin, který je nevědomky vtažen do samého středu několik set let staré záhady, se ocitá na mušce Ivana Ivanoviče Sacharina, ďábelského padoucha, který je přesvědčen, že Tintin ukradl vzácný poklad, spojovaný se zákeřným pirátem zvaným Rudý Rackham. V doprovodu svého psa Filuty, starého mořského vlka kapitána Haddocka a dvojice nešikovných detektivů Kadlece a Tkadlece procestuje Tintin křížem krážem celý svět ve snaze dostat se jako první na místo posledního odpočinku lodi Jednorožec, vraku, který by ve svém nitru mohl skrývat klíč k obrovskému bohatství... A také prastarou kletbu.', 26, null, '103', '2011', 1, 1, 0, 'http://www.csfd.cz/film/236467-tintinova-dobrodruzstvi/', 'http://www.imdb.com/title/tt0983193/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1084, 'Madagaskar 3', 'Madagascar 3: Europe''s Most Wanted', 5, 'Rafinovaným tučňákům se s pomocí hrubé šimpanzí síly podařilo opravit letadlo, ve kterém naši hrdinové havarovali během přeletu z Madagaskaru do Afriky, a vyrazili si užívat do kasina v Monte Carlu. Lva Alexe, hrošici Glorii, zebru Martyho a žirafáka Melmana ale na palubu nevzali, což čtveřici donutí doplavat za nimi po svých Středozemním mořem. Tučňáky nutně potřebují, protože bez tučňáků nemají šanci vrátit se domů. Zejvení divokých, i když přátelských šelem v epicentru evropské zábavy pochopitelně vyvolá rozruch, který dostane za úkol zkrotit kapitánka Chantal DuBois. Je to elitní agentka organizace na odchyt zvěře, pro kterou je její práce posláním. Představa, že svůj životopis vyšperkuje ulovením žirafy, lva, zebry a hrošice ji dovádí až k nadlidskému úsilí a k rozhodnutí dostat se jim na kobylku za každou cenu. V Africe se schovali celkem snadno, na civilizovaném světadíle jim poskytne jediné maskování kočovný cirkus, který se po evropské šňůře chystá i do New Yorku. Jenže staňte se cirkusáky, když nic zvláštního neumíte… Tenhle zpáteční lístek vyjde Martyho, Melamana, Glorii a Alexe hodně draze', 26, null, '90', '2012', 1, 1, 0, 'http://www.csfd.cz/film/252667-madagaskar-3/', 'http://www.imdb.com/title/tt1277953/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1085, 'Doba ledová 4: Země v pohybu', 'Ice Age: Continental Drift', 5, 'Do kin se po třech letech vrací oblíbená mamutí rodinka Mannyho, Ellie, jejich teenagerské dcerunky Broskvičky, lenochoda Sida, šavlozubého tygra Diega a potřeštěného krysoveverčáka Scratta, aby prožili nové napínavé dobrodružství. Právě Scratt, se svou vášní pro žaludy a jejich ukrýváním totiž způsobí katastrofu nevídaných rozměrů. Podaří se mu zasadit svůj žalud takovým způsobem, že pradávná Pangea začne praskat a lámat se na jednotlivé kontinenty a díky pohybu litosférických desek dojde i k rozdělení naší prehistorické party. Ellie a Broskvička zůstanou na jednom kontinentu a Many, Sid a Diego jsou unášeni proudem na ledové kře hluboko do širého oceánu. Ať to stojí, co to stojí, naši tři odvážní přátelé se musí dostat zpátky domů a při tom na ně čeká nejedno velké dobrodružství. Při cestě zpět jim dají zabrat nejenom mořské bouře, přílivové vlny a mořské příšery, ale musejí zvládnout i první společné soužití na jedné malé kře a tak se můžeme těšit na řadu komických scén plných vzájemného špičkování. Shodou okolností a náhod se na jejich provizorní loď, kterou si vytvořili z ledovce, dostane i Sidova babička, kterou skupinka zachrání v příboji mořských vln. Už, už se zdá, že naše partička kamarádů bude zachráněna, když se z oparu mlhy vynoří loď, jenže její podivínská posádka na palubě ani v nejmenším nehodlá našim kamarádům pomoci. Naopak, všichni jsou zajati a dostávají se do ještě větší šlamastiky.', 26, null, '84', '2012', 1, 1, 0, 'http://www.csfd.cz/film/281739-doba-ledova-4-zeme-v-pohybu/', 'http://www.imdb.com/title/tt1667889/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1086, 'Bláznivá komedie', 'Comiche, Le', 2, 'Řemeslo má zlaté dno. Ovšem jen v případě, že ho člověk umí. To se však o dvojici Paolo Villaggio a Renato Pozzeto rozhodně říct nedá. Vrhají se do práce s těmi nejlepšími úmysly, ale všude, kam vlezou, nadělají spoušť. Mají totiž obě ruce levé! Jako údržbáři v kostele moc úspěchu nemají. Jako automechanici na benzínové pumpě zničí několik aut a raději sami odcházejí. Zkoušejí také prodávat vysavače po domech. V jednom hotelu udělají takovou škodu, že tu musí rok pracovat zadarmo. Otevřou si dokonce pohřební ústav, ale po ztracení nebožtíka a zmlácení vdovy to vzdají. Nakonec dostanou nabídku pracovat jako dvojníci, netuší však, že jim jde o život. Jako vždy, když se člověk zaplete s mafií. Ještě, že jejich anděl strážný nad nimi bdí!', 33, null, '86', '1990', 1, 1, 0, 'http://www.csfd.cz/film/32720-blazniva-komedie/', 'http://www.imdb.com/title/tt0099293/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1087, 'Bláznivá komedie 2', 'Comiche 2, Le', 2, 'Paolo a Renato tentokrát slezou z plakátu prvního dílu Bláznivé komedie, aby je nepřelepili novinovou reklamou. Oba naši hrdinové způsobí opět spoustu zmatků, demolicí a především smíchu. Sami uvidíe, co tihle dva svedou jako provozovatelé aerotaxi, legionáři, nebo vánoční dědové.', 33, null, '91', '1992', 1, 1, 0, 'http://www.csfd.cz/film/32719-blazniva-komedie-2/', 'http://www.imdb.com/title/tt0103991/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1088, 'Bláznivá komedie 3', 'Nuove comiche, Le', 2, 'Na úspěšnou Bláznivou komedii 1 a 2 volně navazuje Bláznivá komedie 3, která má díky neodolatelné komičnosti Paola Villagia a Renata Pozzettiho velkou šanci na divácký úspěch. Paolo a Renato opět způsobují katastrofy všude tam, kde se objeví - divadlo, kde pracují jako elektrikáři lehne popelem, dva soudce, které mají chránit před zločinci, přivedou do více než trapné situace, poté se oba "starají" o miminko, které se "náhodně" ocitne před jejich dveřmi... Zamilují se oba do jednoho děvčete a zároveň skončí oba jako strážci majáku na pustém ostrově, kde způsobí poslední a konečnou katastrofu. Blázni se vrací !!!', 33, null, '102', '1994', 1, 1, 0, 'http://www.csfd.cz/film/32746-blazniva-komedie-3/', 'http://www.imdb.com/title/tt0159611/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1089, 'Chyťte ho!', 'Get Shorty', 2, 'Miamský gangster Chili Palmer (John Travolta) se živí vymáháním dluhů a jinými špinavými zakázkami. Jedna z jeho "služebních" cest vede do Hollywoodu, kde má vymáhat peníze od obskurního producenta Harry Zimma (Gene Hackman). Místo toho se však začne plést do příprav jeho následujícího filmu. A jak se ukáže, nic se člověku u filmu nehodí víc, než mafiánská průprava...
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1090, 'Formula 51', '51st State', 2, 'Elmo McElroy (Samuel L. Jackson) příjíždí do Anglie ve skotské sukni, aby zde prodal svou novou drogu na způsob extáze. Na letišti potká ortodoxního fanouška Liverpoolu Felixe DeSouzu (Robert Carlyle), který svou oddanost svému fotbalového týmu prokáže tak, že den před zápasem Liverpool - Manchester vběhne do hospody nacpané fanoušky Manchesteru a nechá jim tam kouřící dýmovnici. Potom poznáváme profesionální vražedkyni a také ex-přítelkyni Felixe Dakotu Philips s krycím jménem Dawn (Emily Mortimer), která je najatá na vraždu McElroye. Ještě se do filmu připlete skupina nácků, kterou si McElroy několikrát výborně a vtipně "ošéfí", a zkorumpovaný detektiv Virgil Kane a tato akční komedie se rozjíždí na plné obátky.', 26, null, '89', '2001', 1, 1, 0, 'http://www.csfd.cz/film/14787-formula-51/', 'http://www.imdb.com/title/tt0227984/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1091, 'Rafťáci', 'Rafťáci', 2, 'Letní teenagerovská komedie režiséra Karla Janáka s Vojtou Kotkem a Jirkou Mádlem v hlavních rolích. Každý z nás si vzpomíná na noční můru dospívajícího teenagera, kterou je dovolená s rodiči. A když se tato noční můra stane skutečností jen díky nešťastné souhře náhod, je to opravdu k vzteku. Dany s Filipem se ale nevzdávají ani v této situaci. Celovečerní film Rafťáci je plný smíchu, napětí a trapasů z nečekaných situací. Poslední dovolená s rodiči, při které kluci zažívají akce, u kterých by rodiče vůbec být neměli. Když Dany slíbil Filipovi, že mu pomůže sbalit holku, netušil, kolik překážek se jim postaví do cesty. Přestože se postupně musejí vyrovnat s přítomností Danyho rodičů, malého brášky Honzíka, přísného instruktora Bořka, objevení dívčího vodáckého kurzu nakonec nabídne nepřeberně možností, jak dosáhnout cíle Filipovy touhy. Co všechno se dá na vodě zažít? Koupání, nuda, peřeje, únava, láska, zklamání či opilost. Rafťáci jsou komedií pro mládež, teenagery, ale svoje si v něm najde i generace jejich rodičů, kterým se po bezstarostnosti prvních lásek už jen stýská.', 27, null, '104', '2006', 1, 1, 0, 'http://www.csfd.cz/film/189604-raftaci/', 'http://www.imdb.com/title/tt0470079/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1092, 'Klub sráčů', 'Kevin & Perry Go Large', 2, 'Kevin a Perry jsou totální outsideři na poli jak sexuálním, tak i DJ''-ském. Pak se jim ale naskytne příležitost jak by se konečně mohli zbavit panictví a zároveň světu dokázat, že jsou špičkový DJ''-ové. Jejich rodiče souhlasí pouze pod jednou podmínkou a to tou, že na Ibizu kde se koná sjezd DJ''-ů a tím pádem i krásných holek, pojedou s nimi. Po příletu na Ibizu se na letišti setkávájí se slavným, ale arogantním DJ''-em Paulem Bulvou, který však dokáže dvěma klukům zajistit popularitu tím, že zremixuje jejich nahrávku a tu pustí v klubu. Brzy poté Kevin s Perrym potkávají dívky svých snů, díky kterým se snad vyřeší problém jejich stálého panictví.', 30, null, '82', '2000', 1, 1, 0, 'http://www.csfd.cz/film/33137-klub-sracu/', 'http://www.imdb.com/title/tt0205177/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1093, '21 Jump street', '21 Jump street', 2, 'Schmidt (Jonah Hill) a Jenko (Channing Tatum) se na střední škole k smrti nenáviděli, aby se z nich v policejní akademii posléze stali nejlepší přátelé. Ačkoliv nepatří k tomu nejlepšímu, čím se může policejní sbor pochlubit, mají možnost svou pověst změnit ve chvíli, kdy jsou přiděleni k tajné policejní jednotce Jump Street pod vedením kapitána Dicksona (Ice Cube). Místo zbraní a policejních odznaků jsou vyzbrojeni batohy, aby se za přispění svého nedospělého vzhledu vmísili tajně mezi mládež. Problém je ale v tom, že dnešní dospívající mládež je docela jiná, než jaká byla před několika lety, a Schmidt s Jenkem zjišťují, že jejich představy o životě středoškoláků, ať už jde o sex, drogy či rock''n''roll, jsou zcela k ničemu. Především ale přicházejí na to, že se stále nevypořádali s celou řadou vlastních problémů, kterým v průběhu dospívání čelili – a oba se musejí postavit hrůze a nervozitě z toho, že se jsou nuceni vrátit znovu na střední školu a znovu zažívat všechny problémy, o kterých se domnívali, že už jsou dávno minulostí.', 26, null, '109', '2012', 1, 1, 0, 'http://www.csfd.cz/film/254016-21-jump-street/', 'http://www.imdb.com/title/tt1232829/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1094, 'Asterix a Obelix ve službách jejího veličenstva', 'Astérix et Obélix: Au Service de Sa Majesté', 2, 'Pod vedením Julia Caesara napadly proslulé římské legie Británii. Jedné malé vesničce se však daří statečně odolávat, ale každým dnem je slabší a slabší. Britská královna proto vyslala svého věrného důstojníka Anticlimaxe, aby vyhledal pomoc u Galů v druhé malinké vesničce ve Francii vyhlášené svým důmyslným bojem proti Římanům… Když Anticlimax popsal zoufalou situaci svých lidí, Galové mu darovali barel svého kouzelného lektvaru a Astérix a Obélix jsou pověřeni doprovodit ho domů. Jakmile dorazí do Británie, Anticlimax jim představí místní zvyky ve vší parádě a všichni to pořádně roztočí! Vytočený Caesar se však rozhodne naverbovat Normanďany, hrůzu nahánějící bojovníky Severu, aby jednou provždy skoncovali s Brity. Jenže Normanďani touží po jediném – zažít strach. A to protože někde zaslechli přísloví: „Strach Ti dává křídla“…a kdo by nechtěl létat? Galové, Britové, Normanďani a Římané se zamotají do bláznivé honičky za kouzelným lektvarem, na němž záleží spása národa…', 32, null, '110', '2012', 1, 1, 0, 'http://www.csfd.cz/film/284211-asterix-a-obelix-ve-sluzbach-jejiho-velicenstva/', 'http://www.imdb.com/title/tt1597522/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1095, 'Profesor v ringu', 'Here Comes the Boom', 2, 'Bývalému univerzitnímu zápasníkovi Scottu Vossovi (Kevin James) je 42 let a bez většího zaujetí učí biologii na upadající střední škole. Když kvůli rozpočtovým škrtům hrozí, že budou zrušeny některé ze školních programů a učitel hudby (Henry Winkler) bude propuštěn, Scott se rozhodne, že jako zápasník bojových umění vydělá škole nějaké peníze navíc. Každý si myslí, že se Scott zbláznil – především školní zdravotní sestra Bella (Salma Hayek) – ale Scott dokáže něco, co nikdo nečekal. Inspiruje své studenty a stane se senzací, která celou školu spojí dohromady.', 26, null, '105', '2012', 1, 1, 0, 'http://www.csfd.cz/film/293178-profesor-v-ringu/', 'http://www.imdb.com/title/tt1648179/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1096, 'Méďa', 'Ted', 2, 'John (Mark Wahlberg) byl jako malý velmi opuštěný, i proto pronesl to pošetilé přání, aby jeho plyšový vánoční dárek začal mluvit. Stal se zázrak a mluvící medvěd se stal mediální hvězdou a miláčkem davů. Jak roky ubíhaly, chlupatému Tedovi zůstal jediný fanoušek – John. Ti dva si náramně notují v pohledu na život (maximální pohoda), na holky (divošky mají přednost), na práci (jen když není zbytí) a John i díky tomu velkoryse přehlíží, že okolí se na jeho soužití s méďou dívá čím dál rozpačitěji. Sežrat mu to dává především jeho holka Lori (Mila Kunis), která je sice nadmíru tolerantní, ale i u ní platí přísloví o chození se džbánem pro vodu. Takže když se ucho utrhne, dostává John nekompromisní ultimátum. Buď Lori, nebo plyšák. A tak se John ocitá na zásadní životní křižovatce, ze které vede jen jedna cesta. Nebo to tak aspoň vypadá.', 26, null, '106', '2012', 1, 1, 0, 'http://www.csfd.cz/film/292988-meda/', 'http://www.imdb.com/title/tt1637725/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1097, 'Stan Helsing', 'Stan Helsing', 2, 'Stan Helsing je smolař, toužící se stát zpěvákem, pracuje však v půjčovně videokazet. O halloweenu dostane za úkol doručit kazetu na maškarní večírek. Do dodávky naloží své přátele, Teddyho v kostýmu supermana, Miu převlečenou za sexy roztleskávačku a ex-přítelkyní Nadine která jde za indiánku. Do cesty se jim však připlete šestice legendárních hororových padouchů. Freddy Krueger, Michael Myers, panenka Chucky, Leatherface, Jasona Voorheese a Pinhead, se spojí v této bláznivé parodii na nejúspěšnější horory všech dob.', 26, null, '108', '2009', 1, 1, 0, 'http://www.csfd.cz/film/255601-stan-helsing/', 'http://www.imdb.com/title/tt1185266/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1098, 'Tohle je válka!', 'This Means War', 2, 'Dva špičkoví agenti CIA, Tuck (Tom Hardy) a FDR (Chris Pine), jsou kamarádi na život a na smrt. To ovšem pouze do doby, než se oba zblázní do jedné a téže slečny - Lauren Scottová (Reese Witherspoon). Kariéristka Lauren Scottová je sice ve svém pracovním životě úspěšná, ale její milostný život je jedna velká katastrofa. Poté, co ji nechá přítel, kvůli kterému se přestěhovala z milované Atlanty, nemá na randění a hledání nového vztahu ani pomyšlení. S tím se ale nespokojí její praštěná kamarádka Trish, která ji přemluví, aby zkusila najít štěstí v lásce na internetové seznamce. Na internetu se seznámí se sympaťákem Tuckem a domluví si s ním rande. Aby to ale neměla tak jednoduché, ve videopůjčovně se jí do cesty připlete i pohledný hezoun FDR. Lauren se rozhodne randit prozatím s oběma, samozřejmě bez nejmenší potuchy, že oba kluci jsou nejlepšími kamarády a navíc top agenty CIA. Tuck a FDR doposud žili život jako v Bondovce - plný zbraní, rychlých aut i krásných žen, které až dosud nikdy nebrali příliš vážně. Tentokrát jsou ale oba přesvědčeni, že našli tu pravou. Zjištění, že se oba zamilovali do stejné dívky, jim ovšem jejich přátelství tak trochu pokazí. I když se dohodnou, že si půjdou z cesty, a Lauren neřeknou, že se znají, nebyli by to správní agenti, aby se vzdali své vysněné dívky tak snadno. Na řadu přichází série promyšlených špiónských útoků s jediným cílem - co nejvíce ztrapnit a zkazit rande toho druhého. Jak tato stále se stupňující bitva dopadne a pro koho se Lauren nakonec rozhodne?', 26, null, '94', '2012', 1, 1, 0, 'http://www.csfd.cz/film/281865-tohle-je-valka/', 'http://www.imdb.com/title/tt1596350/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1099, 'J. Edgar', 'J. Edgar', 7, 'J. Edgar Hoover (Leonardo DiCaprio) založil v roce 1935 FBI (Federální úřad pro vyšetřování) a současně byl i jeho ředitelem téměř 40 let až do své smrti v roce 1972 a prakticky dovedl FBI k její nynější podobě. Byl to svým způsobem i nejmocnější muž v USA, jelikož bylo všeobecně známo, že má ve svém trezoru špínu na téměř každou celebritu nebo vyššího politika. Za svého života bojoval s nejrůznějšími druhy organizovaného zločinu a také byl známý pro svoje sexuální skandály (velká obliba v nošení oblečení druhého pohlaví). Stál v čele zrodu centrálního systému otisků prstů a mnoha dalších. Mimo jiné se o něm vědělo, že mnohdy sám zákony překračoval.', 26, null, '137', '2011', 1, 1, 0, 'http://www.csfd.cz/film/280079-j-edgar/', 'http://www.imdb.com/title/tt1616195/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1100, 'Železná lady', 'Iron Lady', 7, 'Margaret Thatcherová, neobyčejná a všestranná žena, která dokázala, že vysoká politika není jen výsadou mužů, se jako první žena stala premiérkou Spojeného království Velké Británie a Severního Irska. Svým nekompromisním přístupem k politickému, ale i osobnímu životu, získala přezdívku Železná lady. Film ukazuje Margaretino jedenáctileté působení v pozici předsedkyně britské vlády, její neústupnost a rozhodnost, ale i to, jakou cenu zaplatila za politický vliv a moc.', 30, null, '100', '2011', 1, 1, 0, 'http://www.csfd.cz/film/291197-zelezna-lady/', 'http://www.imdb.com/title/tt1007029/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1101, 'Válečný kůň', 'War horse', 7, 'Působivý příběh mladého chlapce jménem Albert a jeho milovaného koně Joeyho se odehrává na samém počátku první světové války. Joey je Albertovým otcem prodán britské jízdě a následně poslán na frontu, kde začíná jeho pozoruhodná pouť na pozadí velké války. Navzdory překážkám, kterým je na každém kroku nucen čelit, Joey ovlivňuje a mění životy všech, se kterými se na své pouti setká. Albert, který na svého přítele nedokáže zapomenout, opouští domov a vydává se na francouzská válečná pole, aby zde svého koně našel a přivedl ho zpátky domů.', 26, null, '141', '2011', 1, 1, 0, 'http://www.csfd.cz/film/277974-valecny-kun/', 'http://www.imdb.com/title/tt1568911/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1102, 'Žena v černém', 'Woman in Black', 7, 'Snímek na motivy klasického duchařského příběhu vypráví příběh Arthura Kippse (Daniel Radcliffe), právníka, který je nucen opustit svého malého syna a odcestovat do odlehlé vesnice, aby zde vyřídil právní záležitosti související s nedávno zesnulým majitelem rezidence Eel Marsh House. Kipps, který o samotě pracuje v tomto starém sídle, začíná odhalovat místní krutá a znepokojivá tajemství a jeho obavy se dále stupňují poté, co zjistí, že místní děti za záhadných okolností umírají. Když pomstychtivá žena v černém začne ohrožovat i jeho nejbližší, je Kipps nucen hledat způsob, jakým vražedný kruh přerušit.', 30, null, '92', '2012', 1, 1, 0, 'http://www.csfd.cz/film/282928-zena-v-cernem/', 'http://www.imdb.com/title/tt1596365/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1103, 'Temné stíny', 'Dark Shadows', 10, 'V roce 1752 vypluli Joshua a Naomi Collinsovi se svým synem Barnabasem lodí z anglického Liverpoolu, aby začali nový život v Americe. Ale ani oceán jim nepomůže v tom, aby unikli záhadnému prokletí, které jejich rodinu pronásleduje. Uplyne dvacet let a Barnabasovi leží svět u nohou - tedy aspoň město Collinsport ve státě Maine. Majitel panství Collinwood - Barnabas je bohatý pán, má moc a je to notorický playboy…dokud neudělá zásadní chybu - zlomí totiž srdce Angelique Bouchard . Angelique je čarodějnice každým coulem. Přichystá mu horší osud, než je samotná smrt: promění ho v upíra a pak ho upálí zaživa. O dvě stě let později se Barnabas nechtěně dostane ze své hrobky a přichází do naprosto jiného světa roku 1972. Vrací se na panství Collinwood a zjistí, že z kdysi velkolepého panství jsou dnes jen ruiny. Zbytku rozpadlé rodiny Collinsů se daří o něco lépe, každý skrývá své temné tajemství. Hlava rodiny Elizabeth Collins Stoddard přivolá blízkou psychiatričku Dr. Julii Hoffman, aby jim pomohla vyřešit rodinné trápení. V usedlosti žije také neúspěšný bratr Elizabeth Roger Collins její pubertální dcera, rebelka Carolyn a Rogerův předčasně vyspělý 10ti letý syn David Collins. Rodinná záhada se přenese i na správce Willieho Loomise, kterého hraje Jackie Earle Haley a na Davidovu novou chůvu Victorii Winters.', 26, null, '113', '2012', 1, 1, 0, 'http://www.csfd.cz/film/243202-temne-stiny/', 'http://www.imdb.com/title/tt1077368/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1104, 'Cesta na tajuplný ostrov', 'Journey 2: The Mysterious Island', 2, 'V tomto pokračování celosvětově úspěšného filmu „Journey to the Center of the Earth,“ z roku 2008 nové rodinné dobrodružství filmu „Journey 2: The Mysterious Island“ ve formátu 3D začíná tím, že mladý Sean Anderson (Josh Hutcherson, který hrál v této roli i v předchozím filmu) dostane zakódované tísňové volání ze záhadného ostrova, z míst, kde by se žádný ostrov nacházet neměl. Je to místo, kde jsou zvláštní formy života, hory ze zlata, životu nebezpečné sopky a nejedno pozoruhodné tajemství. Nový nevlastní otec Hanka (Dwayne Johnson) není schopen syna zadržet a tak se s ním raději vydá na společnou výpravu.  Společně s pilotem helikoptéry (Luis Guzman) a jeho krásnou a rozhodnou dcerou (Vanessa Hudgens), se vydají hledat ostrov, zachránit opuštěné obyvatele ostrova a také uniknout seizmickým vlnám, které tlačí ostrov pod vodu, kde by poklady ostrova byly navždy pohřebeny v moři.', 26, null, '90', '2012', 1, 1, 0, 'http://www.csfd.cz/film/285963-cesta-na-tajuplny-ostrov-2/', 'http://www.imdb.com/title/tt1397514/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1105, 'Mechanik zabiják', 'Mechanic', 4, 'Arthur Bishop je “mechanik” – elitní zabiják s unikátním talentem pro čisté odklízení cílů. Je to práce, která vyžaduje profesionální přesnost a absolutní nestrannost… a Bishop je ten nejlepší. Když však zabijí jeho rádce a kamaráda Harryho, Bishop ztrácí jednu ze svých předností - nezaujatost. Další úkol tak přichází od něj samého: zodpovědní budou pykat! Jeho mise se však zkomplikuje, když se Harryho syn, Steve, k němu přidá se stejnou touhou po pomstě a odhodláním přiučit se Bishopovu řemeslu. Mechanik zabiják jel vždy jen sám na sebe, ale nemůže se obrátit zády k Harryho synovi. Metodický nájemný vrah zavede impulzivního studenta do hloubi svého světa a smrtelné partnerství je zpečetěno.', 26, null, '93', '2011', 1, 1, 0, 'http://www.csfd.cz/film/254158-mechanik-zabijak/', 'http://www.imdb.com/title/tt0472399/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1106, 'X-Men: První třída', 'X-Men: First Class', 4, 'X-Men: První třída se dějově se vrací ještě před film X-Men Origins: Wolverine a odehrává se v polovině minulého století. V době, kdy mladý Charles Xavier (James McAvoy) a Erik Lensherr (Michael Fassbender) byli velmi blízcí přátelé a teprve odkrývali své jedinečné schopnosti. V době studené války, kdy společně s dalšími mutanty museli čelit největší hrozbě, jakou kdy zatím lidstvo poznalo. Proč se ale z nejlepších přátel stali nepřátelé na život a na smrt? Co předcházelo okamžiku, kdy se z Charlese stal Profesor X a z Erika Magneto? Válka mezi X-Meny a Bratrstvem začíná!', 26, null, '126', '2011', 1, 1, 0, 'http://www.csfd.cz/film/251188-x-men-prvni-trida/', 'http://www.imdb.com/title/tt1270798/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1107, 'Skyfall', 'Skyfall', 4, 'Daniel Craig je zpět jako Agent 007 v nové, v pořadí již 23-té bondovce s názvem SKYFALL. Tentokrát bude Bondova loajalita vůči M (Judi Dench) vystavena nelehké zkoušce, když ji začne pronásledovat její vlastní minulost. 007 musí vystopovat a zneškodnit narůstající hrozbu, ať už ho to stojí cokoliv.', 30, null, '143', '2012', 1, 1, 0, 'http://www.csfd.cz/film/234460-skyfall/', 'http://www.imdb.com/title/tt1074638/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1108, '96 hodin: Odplata', 'Taken 2', 4, 'Liam Neeson se vrací jako bývalý agent CIA Bryan Mills, který před dvěma lety v Paříži nemilosrdně pozabíjel gang obchodníků s bílým masem, aby zachránil život své unesené dcery. Nyní ho na výletě v Istanbulu překvapí návštěva dcery Kim a také bývalé manželky Lenore. Poklidná dovolená netrvá dlouho. Lenore je přímo před zraky Bryana unesena. Kim se snaží před pronásledovateli uniknout. Bryan pochopí, že vyjednávání s únosci nemá naději na úspěch. Hlavou gangu je totiž Murad, otec jednoho ze zločinců, které Bryan zabil. Jediné, po čem Murad touží, je krvavá odplata. Pro Bryana začíná neúprosný závod s časem. Musí využít veškeré své znalosti a schopnosti, aby ochránil svou rodinu.', 32, null, '88', '2012', 1, 1, 0, 'http://www.csfd.cz/film/267973-96-hodin-odplata/', 'http://www.imdb.com/title/tt1397280/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1109, 'Rallye smrti: Peklo na zemi', 'Death Race: Inferno', 4, 'Lucas (Luke Goss) – neboli Frankenstein – je legendární jezdec krvavého závodu vězňů známého jako Death Race. Jediné vítězství ho dělí od vytoužené svobody. Čeká ho ale ten nejbrutálnější závod, jaký si lze představit: úplně první Death Race uprostřed pouště. V pekelné výhni jihoafrické pouště Kalahari se Lucas musí utkat s nemilosrdnými soupeři i mocnými zákulisními silami, které usilují o jeho porážku. Ve vedlejších rolích šílené a akcí nabité jízdy Death Race: Peklo na zemi se představují Danny Trejo a Ving Rhames.', 26, null, '101', '2012', 1, 1, 0, 'http://www.csfd.cz/film/300588-rallye-smrti-peklo-na-zemi/', 'http://www.imdb.com/title/tt1988591/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1110, 'Abraham Lincoln: Lovec upírů', 'Abraham Lincoln: Vampire Hunter', 10, 'Odhalte skrytý život Abrahama Lincolna, jednoho z největších amerických prezidentů! Poznejte jeho skutečný příběh, který je plný krve, upířích zubů a nabroušených seker. Vizionářští tvůrci Tim Burton a Timur Bekmambetov nabízejí nový pohled na žánr upírských filmů a právě Abrahama Lincolna představují jako historicky největšího lovce neživých bytostí. Ve filmu, natočeném podle stejnojmenné knihy Setha Grahame-Smithe, odkrýváme dvě tváře jedné z největších postav amerických dějin, uznávaného prezidenta Spojených států amerických a zároveň neústupného lovce upírů. Jeho hnacím motorem je pomsta za smrt milující matky. Inteligentní a vzdělaná žena v malém Abrahamovi již od dětství formovala jeho celoživotní odpor k otroctví. Matčin vliv však neměl dlouhého trvání. Abraham byl svědkem jejího napadení upírem, na jehož následky zemřela. Chladnokrevnou a brutální vraždu má na svědomí netvor, ve kterém chlapec pozná místního podnikatele Jacka Bartise. Rozhodne se ho sprovodit ze světa, jenže bez přípravy málem sám za svůj smělý útok zaplatí životem. Naštěstí jej zachránil Henry Sturges, kultivovaný a charismatický lovec upírů, který vezme Abrahama do svého učení. Henry nemá pochopení pro Abrahamovu touhu po okamžité a bezhlavé pomstě. Naopak jej učí ovládnout vztek a nasměřovat vůli a vášeň nejen k boji s upíry, ale i pro dobro lidstva. Brzy si uvědomí, že existence upírů je v Americe úzce svázána s otrokářským Jihem, kde snadno nakupují černé otroky. Nikoliv na práci na plantážích, ale na jídlo. Konec otroctví by tedy představoval i ohrožení všech upírů. Abraham Lincoln se tak stává nejen mstitelem, ale i politikem bojujícím proti otrokářskému Jihu podporovanému mocnými upírskými vůdci. V jejich čele stojí krvelačný Adam, který je „v civilu“ významným politikem s aristokratickými kořeny. Ten se snaží Abrahama získat na svou stranu. Atmosféra je třaskavá, vše směřuje k velkému střetu Jihu a Severu. Na pozadí ale doutná jiná bitva, bitva lidí a upírů. A na straně zla nemůže Abraham Lincoln nepotkat i vraha své matky, Jacka Bartise.', 26, null, '105', '2012', 1, 1, 0, 'http://www.csfd.cz/film/282840-abraham-lincoln-lovec-upiru/', 'http://www.imdb.com/title/tt1611224/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1111, 'Šerifové', 'U.S. Marshals', 4, 'Muž, považovaný za bezohledného a tajemného vraha (Wesley Snipes) je na útěku před zákonem a snaží se zjistit, díky komu byl obviněn ze dvou vražd, spáchaných v New Yorku. nemá však mnoho času... zkušený šerif Sam Gerard (Tommy Lee Jones) a jeho muži jsou mu na stopě. Nelítostné pronásledování začíná...', 26, null, '126', '1998', 1, 1, 0, 'http://www.csfd.cz/film/12251-serifove/', 'http://www.imdb.com/title/tt0120873/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1112, 'Safe', 'Safe', 4, 'Desetiletá čínská dívka Mei je matematický génius. Jednoho dne je násilně odvedena do New Yorku, kde má při vyděračských kšeftech sloužit jako “počítadlo”. Luke Wright (Jason Statham), zápasník bojových umění z New Jersey, zpacká zmanipulovaný zápas a ruská mafi e pro výstrahu zavraždí jeho ženu a každého, s kým se pokusí navázat jakékoli přátelství či vztah. A tak se Luke, bez domova, prostředků a naprosto izolovaný od společnosti, potuluje ulicemi a balancuje na pokraji sebevraždy. Náhle však jeho cestu zkříží malá vyděšená Číňanka pronásledovaná ruskými gangstery. Luke v mžiku stojí před zásadní otázkou: Pokračovat po vlastní ose vedoucí do záhuby nebo se vrátit zpět a bojovat s mafiánským podsvětím za záchranumalé dívky, kterou nikdy dříve nepotkal?', 26, null, '94', '2012', 1, 1, 0, 'http://www.csfd.cz/film/280007-safe/', 'http://www.imdb.com/title/tt1656190/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1113, 'Nezvratný osud', 'Final Destination', 8, 'Smrt je někde nablízku a Alex Browning má to štěstí (nebo smůlu?), že ví kdy a kde se zubatá objeví. Alexovo zvláštní nadání se poprvé projeví těsně po nástupu na palubu letadla, kterým má, společně se svými spolužáky ze střední školy, letět na školní výlet do Paříže. A právě když už sedí v kabině a se zapnutým bezpečnostním pásem čeká na odlet, promítne se mu před očima hrozivá vize, ve které prožije start letadla následovaný explozí, již nikdo z pasažérů nepřežije. Alex propadne panice a vyzývá všechny, aby okamžitě vystoupili, čímž způsobí mezi pasažéry takový rozruch, že jej společně s dalšími šesti spolucestujícími posádka vykáže z letadla. Kromě Alexe se tak ocitnou zpátky na letišti jeho kamarádi Tod a Billy, spolužačka Clear, která instinktivně vycítila, že Alexovy obavy jsou oprávněné, Carter, který se s Alexem v letadle popral a byl za to vyveden i se svou přítelkyní Terry, a jejich učitelka, slečna Lewtonová, která se dobrovolně rozhodla, že se svými studenty počká na další let. Atmosféru vzájemného obviňování za ztrátu půldne v Paříži přeruší výbuch letadla, ke kterému skutečně krátce po startu dojde. Nikdo nechápe, jak mohl Alex katastrofu předvídat, a nakonec, místo aby mu byli vděční, se ho spíš bojí a hledí na něj s nevraživostí. I agenti FBI, kteří ho vyšetřují, si jeho vysvětlení vyslechnou s nedůvěrou a spíše si Alexe zařadí mezi podezřelé. Zvláště když po několika týdnech začínají umírat další z těch, co díky Alexovi přežili. Alex postupně nabývá přesvědčení, že díky jeho předtuše se jim všem sice podařilo smrt obelstít, ale ne nadlouho. Proto se spojí se zbývajícími kamarády, aby společnými silami čelili osudu a vyzráli nad smrtí.', 26, null, '98', '2000', 1, 1, 0, 'http://www.csfd.cz/film/29943-nezvratny-osud/', 'http://www.imdb.com/title/tt0195714/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1114, 'Nezvratný osud 2', 'Final Destination 2', 8, 'Clear Riversová (Ali Larterová) jediná prežila katastrofu letu č. 180. Všetci ostatní pasažieri, ktorí rovnako ako ona najprv unikli pádu lietadla, sa neskôr jeden po druhom odporúčali do chladnej náruče smrti. Nezvratný osud si postupne vybral ich životy a vyrovnal tak číslo náhodne prežitých takmer na nulu. Clear vie, že ona je teraz na rade. Že smrť sa na ňu už chystá a len náhoda ich môže zachrániť. Bohužiaľ je jediná, ktorá tomu verí. Všetcia ostatní si myslia, že šok zo smrti jej priateľov bol natoľko silný až stratila súdnosť. Preto ju teraz držia pod zámkom na psychiatrickej klinike, kde si ju môže smrť nájsť pri jednej a tisícej príležitosti. No smrť má zatiaľ inde plné ruky práce. Kimberly Cormanová (A.J.Cooková) má zvláštnu víziu - ona a jej priatelia zomreli na ceste č. 23 pri strašnej zrážke áut, kde lietali časti tiel spolu s kovom a plastovými úlomkami. Bol to nejaký psychický záchvat strachu? Alebo snáď varovanie? Kimberly je na najlepšej ceste do ústavu za Clear. Dožije sa toho vôbec?', 26, null, '90', '2003', 1, 1, 0, 'http://www.csfd.cz/film/82444-nezvratny-osud-2/', 'http://www.imdb.com/title/tt0309593/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1115, 'Nezvratný osud 4', 'Final Destination 4', 8, 'V den, který měl být pohodovým dnem s kamarády na závodech, měl Nick Bannon (Bobby Campo) děsivou předtuchu, že shoda nešťastných náhod způsobí hromadnou havárii závodících aut, že hořící trosky aut budou padat mezi diváky a brutálně zabijí i jeho kamarády a že na něj samotného spadne střecha ochozů. Když se Nick probere ze své hrůzostrašné představy, zpanikaří a přiměje svou přítelkyni Lori (Shantel VanSanten) a jejich kamarády Janet (Haley Webb) a Hunta (Nick Zano), aby odešli…podaří se jim uniknout jen vteřiny před tím, než se Nickova strašná vize stane děsuplnou realitou. Skupinka mladých lidí si myslí, že se jim povedlo podvést smrt, ale bohužel pro Nicka a Lori to je jen začátek. Zlé předtuchy pokračují a přeživší ze závodů umírají jeden po druhém, a to nepředstavitelně krutě. Nick musí přijít na to, jak znovu podvést smrt - a tentokrát definitivně - ještě před tím, než se sám stane obětí svého nezvratného osudu. Film je posledním pokračováním série „Nezvratný osud“ a je prvním 3D filmem, který svým fanouškům nabídne velmi děsivou, vzrušující jízdu.', 26, null, '78', '2009', 1, 1, 0, 'http://www.csfd.cz/film/242330-nezvratny-osud-4/', 'http://www.imdb.com/title/tt1144884/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1116, 'Nezvratný osud 5', 'Final Destination 5', 8, 'Nezvratný osud 5 je pokračováním hororové série. Tentokrát skupinu nesleduje žádný démon ani šílenec, ale samotná smrt, či přesněji řečeno samotní filmaři. Skutečná smrt je samozřejmě daleko prozaičtější, když nechá spoustu lidí umřít ve spánku v posteli, ale to není příliš kinematografické. V tomto filmu, podobně jako v předchozích čtyřech dílech, napětí či provokování spočívá v tom, že člověk hádá, které předměty na určitém místě zlikvidují určitou postavu - špatné vedení, záludné nože, větrák u stropu nebo požár v kuchyni?
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1117, 'Blesk', 'Blitz', 4, 'Zabiják poldů versus polda zabiják. Na svobodě řádí sériový vrah. Každý den umírá další policista. Když zákon nedokáže své vlastní příslušníky ochránit, potřebuje muže, který porušuje veškerá pravidla. Tím je nekompromisní londýnský policista Brant (Jason Statham) v týmu se svým kolegou, strážníkem Nashem (Paddy Considine), kterého nemá příliš v oblibě. Čas je k nim však nemilosrdný, každý den přibývá další oběť', 30, null, '97', '2011', 1, 1, 0, 'http://www.csfd.cz/film/265655-blesk/', 'http://www.imdb.com/title/tt1297919/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1118, 'Resident Evil: Odveta', 'Re5ident Evil: Retribution', 4, 'Smrtelný virus společnosti Umbrella Corporation dál pustoší Zemi a mění světovou populaci na legie masa, které jedí Nemrtví. Jediná a poslední naděje lidské rasy v podobě ALICE (Milla Jovovich), se probudí v srdci nejtajnější operace společnosti Umbrella a odkrývá víc o své tajemné minulosti, jak dále pátrá v komplexu. Alice, bez bezpečného útočiště, pokračuje v honbě na ty, kteří jsou odpovědni za šíření epidemie; honbě, která jí dovede z Tokya do New Yorku, Washingtonu a Moskvy a vyvrcholí ohromujícím odhalením, které ji donutí znovu přemýšlet o všem, o čem si dosud myslela, že je pravda. Alice, které pomáhají noví spojenci a staří přátelé, musí bojovat tak dlouho, aby přežila a unikla nepřátelskému světu na pokraji zapomnění. Konec se blíží.', 26, null, '92', '2012', 1, 1, 0, 'http://www.csfd.cz/film/293062-resident-evil-odveta/', 'http://www.imdb.com/title/tt1855325/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1119, 'Ocelová pěst', 'Real Steel', 4, 'V akčním snímku z blízké budoucnosti Ocelová pěst představuje Hugh Jackman Charlieho Kentona, bývalého boxera, jehož milovaný sport ovládli dva a půl metru vysocí oceloví roboti a lidé jako Charlie už do něj nadále nijak nezapadají. Protože nemá s kým zápasit a nemá žádné vyhlídky na lepší budoucnost, je nucen přivydělávat si jako promotér robotických zápasů. Daří se mu jakž takž protloukat tím, že dává dohromady mizerné a vysloužilé roboty a cestuje od jednoho turnaje k druhému, kamkoliv, kde je schopen si nějak přivydělat. Když už se zdá, že situace nemůže být zoufalejší a komplikovanější, vpadne mu znenadání a proti jeho vůli do života jeho syn Max (Dakota Goyo), se kterým už delší dobu není v kontaktu. Oba společně spojí síly, aby postavili a vytrénovali nového robota a proměnili ho v boxerského šampiona. Zatímco si buduje jméno v brutálním a nemilosrdném světě robotických zápasů, zjišťuje Charlie, že se mu navzdory všem očekáváním nabízí poslední šance na boxerský comeback.', 26, null, '122', '2011', 1, 1, 0, 'http://www.csfd.cz/film/267018-ocelova-pest/', 'http://www.imdb.com/title/tt0433035/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1120, 'Hugo a jeho velký objev', 'Hugo', 11, 'Hugo Cabret (Asa Butterfield) je neobyčejný kluk, který po otcově smrti žije na pařížském vlakovém nádraží u strýce alkoholika, jenž tu pracuje jako seřizovač nádražních hodin. Po tátovi Hugo zdědil vynalézavost a robota s klíčovou dírkou ve tvaru srdce, který možná ožije, pokud se do ní vloží ten správný klíč, a předá chlapci otcův vzkaz. Alespoň v to doufá. Neutišitelná zvědavost přivede Huga k nerudnému prodavači mechanických hraček (Ben Kingsley) a od něj i k jeho chráněnce (Chloe Grace Moretz), která, ač je to neuvěřitelné, drží v rukou klíč k rozluštění Hugovy životní záhady. Jak k němu přišla? Jaké tajemství skrývá její adoptivní prarodič, že o něm nesmí nikdo mluvit? Skrývá robot skutečně otcův vzkaz? A dokáže Hugo na všechny tyhle otázky odpovědět dřív, než ho dostihne ruka zákona v podobě přísného nádražního hlídače (Sacha Baron Cohen) a pošle ho do sirotčince?', 26, null, '121', '2011', 1, 1, 0, 'http://www.csfd.cz/film/242004-hugo-a-jeho-velky-objev/', 'http://www.imdb.com/title/tt0970179/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1121, 'Sněhurka a lovec', 'Snow White and the Huntsman', 11, 'Mocná čarodějnice Ravenna (Charlize Theron) se lstí vetřela do přízně krále prosperující a mocné říše, který z ní záhy udělá královnu a pak zemře. Ravenně ale podle proroctví kouzelného zrcadla stojí ve štěstí nevlastní dcera Sněhurka (Kristen Stewart) a posedlost vlastní krásou, kterou by si ráda uchovala navěky. Všechny problémy by vyřešila Sněhurčina smrt, jenže dívka právě uprchla z domácího vězení do Temného lesa, odkud se ještě nikdo živý nevrátil. Ale královna potřebuje mít jistotu, a tak si najme odvážného lovce (Chris Hemsworth), který dostane za úkol ji vystopovat a přivést zpátky. Jeho sebevražednou misi komplikují nejen podivuhodní obyvatelé, kteří les obývají, ale také to, že Sněhurka není jen tak obyčejná holka. Klidu na práci lovci nepřidává ani statečný princ (Sam Claflin), jenž pro ni má slabost už od dětství, a hlavně partička sedmi trpaslíků, kteří sice nejsou tak roztomilí jako Prófa a Stydlín, zato se umí náramně ohánět se sekyrou a vědí, že se Sněhurkou jim svitla naděje na záchranu temnotou zmítané říše.', 26, null, '127', '2012', 1, 1, 0, 'http://www.csfd.cz/film/287718-snehurka-a-lovec/', 'http://www.imdb.com/title/tt1735898/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1122, 'Válka Bohů', 'Immortals', 10, 'Dlouhé věky od dob, kdy Bohové vyhráli svůj mýtický zápas proti Titánům, se zjevuje nové zlo zmítající světem. Šílený touhou po moci, započal král Hyperion (Mickey Rourke) válku proti lidskosti. Hledá bájný luk Epirus, zbraň nepředstavitelné moci, kterou ukoval bůh Áres. Aby luk našel, shromažďuje krvežíznivou armádu vojáků znetvořených jeho vlastní rukou a začíná válečné běsnění a kruté drancování starého Řecka. Jen ten, kdo vlastní tento luk, je schopen vysvobodit Titány lačnící po pomstě, od úsvitu věků uvězněné hluboko v kameni hory Tartaros. V rukou krále Hyperiona znamená luk zkázu lidstva a pokoření moci bohů - ti totiž dle prastarého zákona nesmějí zasahovat do sporů smrtelníků. Jsou tedy bezmocní, ale jen do chvíle, kdy se objevuje jejich jediná naděje, venkovan Theseus (Henry Cavill).
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1123, 'Tři mušketýři', 'Three Musketeers', 11, 'Postavy čtyř hrdinů bez bázně a hany, kteří se bijí ve jménu francouzského krále a snaží se překazit intriky pletichářského kardinála, zrádného vévody z Buckinghamu a dvojité agentky milady de Winter, snad netřeba představovat. Z původní románové epopeje Alexandra Dumase staršího, četných převyprávění i nespočtu filmových převedení je všechny důkladně známe. Paul W. S. Anderson ve své moderní interpretaci ctí reálie příběhu a své novodobé ztvárnění hodlá přiblížit výpravnosti původního literárního díla.', 30, null, '106', '2011', 1, 1, 0, 'http://www.csfd.cz/film/278300-tri-musketyri/', 'http://www.imdb.com/title/tt1509767/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1124, 'Bitevní loď', 'Battleship', 4, 'Hopper (Taylor Kitsch) nikdy nechtěl sloužit u válečného námořnictva jako jeho starší bratr. Kvůli velkému průšvihu ale musel. Nechtělo se mu ani požádat o ruku své holky (Brooklyn Decker) jejího otce (Liam Neeson), shodou nešťastných náhod svého nejvyššího velitele. Bude muset. Nikdy také nechtěl bojovat proti mimozemské invazi. I k tomu ho okolnosti donutí. Pokud ne, všichni končíme. Peter Berg, režisér akční komedie Hancock a thrilleru Království, na Zemi přivedl další nepřátelsky naladěné mimozemšťany. S nimi si to na férovku rozdají stateční námořníci, v jejichž řadách najdete i sličnou dělostřelkyni Raikesovou, v „civilu“ popovou hvězdu Rihannu. Všichni se stali obětmi ambiciózního vědeckého experimentu, jehož cílem bylo nalezení inteligentního života kdekoli ve vesmíru. Vysílaný signál skutečně na jedné planetě zachytili a vyslali několik průzkumných lodí, které měly zjistit, odkud se to ozývá a jak to tam vypadá. Mimozemšťané přistáli na moři poblíž Havajských ostrovů, uprostřed probíhajícího mezinárodního námořního cvičení, které si interpretovali po svém a začali se bránit. Kolem své flotily a na části pobřeží utvořili neprodyšný ochranný štít, pod kterým s nimi uvízlo i několik bitevníků (včetně toho, na kterém slouží Hopper) a také Hopperova přítelkyně, která byla zrovna na treku po havajských kopcích, poblíž vysílače výše uvedeného signálu. Hopper má tedy pořádný důvod pustit se do boje. Přestřelka do posledního náboje mezi „našimi“ a mimozemskými bitevníky bude mít stejné grády jako pošťuchování Autobotů a Decepticonů v Transformers.', 26, null, '133', '2012', 1, 1, 0, 'http://www.csfd.cz/film/269236-bitevni-lod/', 'http://www.imdb.com/title/tt1440129/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1125, 'Hunger Games', 'Hunger Games', 4, 'V troskách bývalé Severní Ameriky se rozkládá země Panem s nablýskaným hlavním městem Kapitolem a dvanácti okolními kraji. Kapitol jim vládne krutou a tvrdou rukou. Poslušnost si udržuje kláním Hunger games – zvrácenou televizní reality show, ve které každoročně jedna dívka a jeden chlapec z každého kraje nedobrovolně bojují o život. Šestnáctiletá Katniss chce zachránit svou sestru, která byla vylosována a nabídne svou účast dobrovolně. Do hry o přežití se přidávají i lidskost a láska… Dokáže přežít v divočině, když se všichni snaží o to, aby nespatřila další úsvit?', 26, null, '137', '2012', 1, 1, 0, 'http://www.csfd.cz/film/291670-hunger-games/', 'http://www.imdb.com/title/tt1392170/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1126, 'Elitní zabijáci', 'Killer Elite', 4, 'Pohádkově bohatému ománskému šejkovi zbývá posledních pár měsíců života. Než mu vyměřený čas vyprší, chce dostat na talíři hlavy mužů, kteří zabili jeho tři syny. Nabízí svá ropná pole, což je velmi lákavá nabídka, ovšem odpovídá nelehkému zadání. Obětmi šejkovy odplaty se mají stát příslušníci britských speciálních jednotek (SAS), elitní, všestranně vybavení vojáci, kteří jeho hochy připravili o život během války o Omán. Tuhle pracovní nabídku, ovšem bez konkrétních detailů, dostane Hunter (Robert De Niro), legenda mezi nájemnými zabijáky. Když po příletu do Ománu zjistí, že má zlikvidovat příslušníky SAS, nechce úkol vzít. S tím se tak trochu počítalo, protože Hunter má být jen návnadou ve složité hře, jejímž cílem je najmout na tenhle mimořádný úkol Dannyho (Jason Statham). Hunterův žák a přítel je ve svém oboru absolutní jednička, bohužel ho před časem pověsil na hřebík. Pokud práci vezme a zabije tři esa SAS, zůstane Hunter naživu. V opačném případě má smůlu. A protože Dannymu kdysi zachránil život, je jasné, co se bude dít.
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1127, 'Lara Croft - Tomb Raider: Kolébka života', 'Lara Croft Tomb Raider: The Cradle of Life', 4, 'Angelina Jolie musí najít Pandořinu skříňku dříve, než se jí zmocní šílený vědec. Ten ji chce využít k získání neomezené vlády nad světem.
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1128, 'Colombiana', 'Colombiana', 4, 'Jako dítě byla přítomna vraždě svých rodičů v Bogotě. Nyní se z ní stává chladnokrevný vrah.', 26, null, '104', '2011', 1, 1, 0, 'http://www.csfd.cz/film/287454-colombiana/', 'http://www.imdb.com/title/tt1657507/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1129, 'Looper', 'Looper', 4, 'Loopeři jsou nájemní zabijáci, kteří pracují pro zločineckou organizaci. Jejich úkol je tradiční: osoby, které jsou pro sdružení nějakým způsobem nepohodlné, zastřelí a zbaví se těla. Odměnu za vykonanou práci pak vždy najdou přímo u své oběti. A za svou krvavou práci jsou placeni opravdu velice dobře. Existuje pro ně pouze jediné pravidlo – za žádnou cenu nikdy nesmí nechat svůj cíl utéct. Joe (Joseph Gordon-Levitt) patří mezi loopery, spolehlivě vykonává svou práci a užívá si luxusního, bezstarostného života. A to platí až do chvíle, kdy dostane další zakázku. K jeho velkému překvapení se totiž před jeho připravenou zbraní objeví on sám, jen o něco starší (Bruce Willis). Tato situace se mohla přihodit jedině díky další zvláštnosti této práce – organizace looperům totiž jejich „klienty“ posílá z budoucnosti. Popravované oběti jsou transportovány zpět v čase a to přímo před hlaveň určeného loopera. A Joeovi zbývá jen zlomek vteřiny k zásadnímu rozhodnutí – má zastřelit sám sebe?', 26, null, '114', '2012', 1, 1, 0, 'http://www.csfd.cz/film/247870-looper/', 'http://www.imdb.com/title/tt1276104/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1130, 'Sherlock Holmes: Hra stínů', 'Sherlock Holmes: A Game of Shadows', 11, 'Sherlock Holmes býval nejchytřejším mužem salónu... až dosud. Na scéně je ovšem nový kriminalistický mozek - profesor Moriarty (Jared Harris), který se intelektuálně Holmesovi nejen vyrovná, ale jeho cit pro špatnost spolu s naprosto chybějícím pocitem svědomí mu vlastně může pomoci slavného detektiva předstihnout. Když najdou rakouského korunního prince mrtvého, podle důkazů, jak je interpretuje inspektor Lestrade (Eddie Marsan), to vypadá na sebevraždu. Ale Sherlock Holmes usuzuje, že princ se stal obětí vraždy, která je navíc pouze kouskem větší a daleko a zlověstnější záhady, kterou zkonstruoval profesor Moriarty. Při výkonu služby spojené se zábavou Holmes najde stopu v podzemním pánském klubu, kde Holmes spolu se svým bratrem Mycroftem Holmesem (Stephen Fry) zapíjejí poslední noc svobody Dr. Watsona. Právě tady se Holmes seznámí se Sim (Noomi Rapace), cikánskou kartářkou, která vidí víc, než prozrazuje. Vyšetřování se stává ještě nebezpečnějším, když se Holmes, Watson a Sim dostávají na starý kontinent. Prohnaný Moriarty je pořád o krok napřed a spřádá sítě smrti a zkázy. Všechno je součástí většího plánu, který, pokud vyjde, změní celé dějiny.', 26, null, '123', '2011', 1, 1, 0, 'http://www.csfd.cz/film/273969-sherlock-holmes-hra-stinu/', 'http://www.imdb.com/title/tt1515091/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1131, 'Underworld: Probuzení', 'Underworld: Awakening', 4, 'Od chvíle, kdy Selene se svým lidsko-lykanským milencem Michaelem zničili prastarého upíra Marcuse ve filmu Underworld: Evolution, uběhlo již patnáct let. V jejich průběhu lidstvo odhalilo existenci jak upírů, tak lykanů, a rozpoutalo válku s cílem obě rasy vyhladit. Selene, která byla v průběhu genocidy zajata, se po více než deseti letech probouzí jako vězeň v neprodyšně uzavřené laboratoři společnosti Antigen, mocné korporace, která se snaží vyvinout vakcínu proti virům, jenž mají na svědomí existenci upírů a lykanů. Selene je zničena zjištěním, že Michael zemřel, ale smutek je velice záhy překonán šokujícím zjištěním, že v průběhu svého kryogenního spánku porodila dceru Eve (India Eisley). Ve snaze najít úkryt pro sebe i své dítě se Selene ocitá ve světě, kde jsou příslušníci její kdysi tak hrdé rasy na hranici vyhubení a zbytky přežívajících se ukrývají v podzemí. Odvržena zbývajícími klany se Selene rozhodne spojit své síly s mladým upírem Davidem (Theo James), který se k ní připojí v krvavém tažení proti Antigenu, jenž se snaží zničit ji i její dítě. Když jí na stopu přijdou také její obvyklí nepřátelé, lykani, zdá se, že je i Selene naprosto bezmocná proti nejzákeřnějšímu protivníkovi v historii série Underworld: geneticky vylepšenému superlykanovi.', 26, null, '85', '2012', 1, 1, 0, 'http://www.csfd.cz/film/273581-underworld-probuzeni/', 'http://www.imdb.com/title/tt1496025/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1132, 'Muži v černém 3', 'Men in Black III', 2, 'Ve filmu Muži v černém 3 jsou známí agenti J (Will Smith) a K (Tommy Lee Jones) zpět... A opět včas. J během svého patnáctiletého fungování v agentuře Muži v černém viděl spoustu nevysvětlitelných věcí, ale nic - ani mimozemšťané - ho nedokáže tak zmást jako jeho sarkastický tajnůstkářský partner. Ale když je život agenta K i osud planety Země v ohrožení, musí agent J cestovat v čase zpět do minulosti, aby dal vše do pořádku. J zjistí, že existují tajemství vesmíru, o kterých mu K nikdy neřekl. A ona tajemství budou odhalena, když se v minulosti spojí s mladým agentem K (Josh Brolin), aby zachránil svého partnera, agenturu a budoucnost lidstva.', 26, null, '102', '2012', 1, 1, 0, 'http://www.csfd.cz/film/264588-muzi-v-cernem-3/', 'http://www.imdb.com/title/tt1409024/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1133, 'Bourneův odkaz', 'Bourne Legacy', 4, 'V Bourneovu ultimátu agent Jason Bourne zlikvidoval tajný a kontroverzní vládní program společně s jeho tvůrci. Šlo však jen o špičku ledovce, pod níž se ukrývá mnohem sofistikovanější systém tajných operací, kterému šéfuje Byer (Edward Norton). Chladnokrevného profíka události kolem Jasona Bournea rozhodně nenechávají chladným, a tak se pro jistotu rozhodne bezpečně pohřbít podobný program dřív, než mu taky přeroste přes hlavu. K jeho smůle však jinak precizní čistící operaci přežije právě Aaron Cross a také vědkyně Marta Shearing (Rachel Weisz), která měla na starost jeho vědeckou část. Ti dva se navíc spojí a to je pro Byera smrtící hrozba, mnohem větší než ta, kterou představuje Bourne. Cross je taky z pořádně nepoddajného materiálu a oproti Jasonovi má tu výhodu, že si všechno pamatuje.', 26, null, '134', '2012', 1, 1, 0, 'http://www.csfd.cz/film/248751-bourneuv-odkaz/', 'http://www.imdb.com/title/tt1194173/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1134, 'Temný rytíř povstal', 'Dark Knight Rises', 4, 'Je tomu již osm let, co se Batman vytratil do noci, čímž se tak z hrdiny změnil v uprchlíka. Tím, že za něj padla vina za smrt Harveyho Denta, obětoval temný rytíř všechno pro to, co společně s komisařem Gordonem považoval za vyšší dobro. A na čas tato lež skutečně zabírala, když byla kriminální aktivita v Gothamu rozdrcena silou Dentova odkazu. 
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1135, 'Vyměřený čas', 'In Time', 4, 'Představte si, že můžete žít jen do 25 let. Pak zemřete. Pokud nevyděláte, neukradnete nebo nezdědíte nějaký čas navíc. Děj filmu akčního scifi thrilleru Vyměřený čas v hlavní roli s Justinem Timberlakem (Kamarád taky rád, Zkažená úča, The Social Network) se odehrává v blízké budoucnosti, v době, kdy peníze neznamenají vůbec nic. Platí se časem. Will Salas (Justin Timberlake) má na svých životních hodinách zřídkakdy víc než 24 hodin. Žije z minuty na minutu v chudé zóně Dayton a jenom tvrdá práce v továrně mu každý den poskytne další den života. Stejnému osudu čelí i jeho matka Rachel (Olivia Wilde) a spousta dalších lidí v tomto zapadákově. Místní gangy jsou navíc vždy připraveny kohokoliv okrást, byť jen o pár hodin, a klidně tím ukončit jeho život. Jednoho dne Will pomůže před jedním z těchto gangů uniknout bohatému Henrymu (Matt Bomer). Henry za to Willovi daruje celé století času a sám se rozhodne svůj nekonečný život skončit. Will je následně obviněn z jeho vraždy a nezbývá mu nic jiného, než dokázat svoji nevinu. Vydává se na útěk do nejbohatší zóny v New Greenwich, kde na rozdíl od Daytonu nikdo nikam nespěchá. Čas je luxus, který si tu mohou dovolit všichni, protože budou žít stovky ba dokonce tisíce let. V New Greenwich je ale zároveň někdo, kdo Willa přitahuje. Sylvia (Amanda Seyfried) je dcera mimořádně bohatého Philippa Weise (Vincent Kartheiser), která se ve chvíli, kdy Willa přicházejí zatknout strážci času, stává ideálním rukojmím. Vysoká hra o změnu pravidel nespravedlivého světa může začít.', 26, null, '105', '2011', 1, 1, 0, 'http://www.csfd.cz/film/281357-vymereny-cas/', 'http://www.imdb.com/title/tt1637688/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1136, 'Útěk z MS-1', 'Lockout', 4, 'Lidstvo našlo způsob, jak ochránit svět před zločinci. Ti nejhorší z nejhorších jsou drženi na místě, ze kterého není úniku. Mimo Zemi, uprostřed hlubokého vesmíru. Prezidentova dcera Emilie Warnock navštíví v rámci humanitární mise vesmírnou věznici MS One. Ale krvavá vzpoura vězňů z Emilie učiní to nejcennější rukojmí. Ven ji může dostat jen jediný člověk. Bývalý špičkový vládní agent Snow je svéhlavý a nekompromisní. Jeho současná pozice na špionážním trhu je, mírně řečeno, špatná - čelí obvinění ze spiknutí proti USA. Od prezidenta dostává jedinečnou nabídku: nový život a svobodu výměnou za záchranu první dcery Spojených států. Jenže v sázce je mnohem více…', 32, null, '91', '2012', 1, 1, 0, 'http://www.csfd.cz/film/285322-utek-z-ms-1/', 'http://www.imdb.com/title/tt1592525/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1137, 'Prometheus', 'Prometheus', 4, 'Dva mladí, úspěšní archeologové Elisabeth Shaw (Noomi Rapace) a Charlie Holloway (Logan Marshall-Green) objevili na různých místech Země několik jeskynních piktogramů z dávných civilizací a postupně se jim podařilo rozkrýt, že všechny piktogramy poukazují na jedno jediné místo ve vzdáleném vesmíru. S týmem dalších badatelů se tak na palubě kosmické lodi Prometheus vydávají na cestu, na jejímž konci chtějí nalézt odpovědi na nejzákladnější a nejniternější otázky lidského bytí. Oba však mají poněkud rozdílnou motivaci, proč se expedice účastní. Elisabeth věří, že lidstvo bylo stvořeno vyšší mocí a chce se se stvořiteli setkat a pochopit původ lidské existence, zatímco Holloway se převážně snaží uvést věci na pravou míru. Nikdo z posádky ovšem není připraven na nepředstavitelnou hrůzu, která na ně čeká a netuší, že právě oni budou muset svést děsivý boj o záchranu budoucnosti lidské rasy.', 26, null, '119', '2012', 1, 1, 0, 'http://www.csfd.cz/film/290958-prometheus/', 'http://www.imdb.com/title/tt1446714/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1138, 'Total Recall (2012)', 'Total Recall', 4, 'Ačkoli má tovární dělník jménem Quaid (Colin Farrell) krásnou manželku (Kate Beckinsale), kterou miluje, výlet do mysli mu připadá jako perfektní dovolená od frustrujícího života – skutečné vzpomínky na život super špióna mohou být přesně to, co potřebuje. Ale když se proces zvrtne, Quaid se stane hledaným mužem. Quaid, který je na útěku před policií – kterou řídí kancléř Cohaagen (Bryan Cranston), vůdce svobodného světa – se spojí s povstaleckou bojovnicí (Jessica Biel), aby našel hlavu ilegálního odboje (Bill Nighy) a zastavil Cohaagena. Linie mezi fantazií a realitou se začne smazávat a osud jeho světa visí na vlásku, když Quaid objeví svou skutečnou identitu, svoji pravou lásku a svůj skutečný osud.', 26, null, '113', '2012', 1, 1, 0, 'http://www.csfd.cz/film/268012-total-recall/', 'http://www.imdb.com/title/tt1386703/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1139, 'Dredd', 'Dredd', 4, 'Budoucnost. Z Ameriky je ozářená pustina. Na jejím východním pobřeží, od Bostonu po Washington, D.C., se rozkládá Mega City One, rozsáhlá megalopole plná násilí, ve které více než 400 milionů obyvatel přežívá v neustálém strachu. Na pořádek v ulicích dohlíží takzvaní Soudci – vymahači práva, porotci, soudci i vykonavatelé rozsudku v jedné osobě. Takovým Soudcem je i Dredd (Karl Urban), který od svého nadřízeného dostává za úkol vyzkoušet v praxi novou Soudkyni Cassandru Anderson (Olivia Thirlby). Čeká je zdánlivě snadná práce – vražda v nechvalně proslulém dvousetpatrovém slumu, který provozuje bezcitný klan Ma-Ma. Když se ale pokusí zatknout jednoho z přisluhovačů klanu, Ma-Ma neprodyšně uzavře celou budovu. Oba Soudci se ocitají uprostřed brutálního a nelítostného souboje o přežití.', 30, null, '95', '2012', 1, 1, 0, 'http://www.csfd.cz/film/266803-dredd/', 'http://www.imdb.com/title/tt1343727/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1140, 'Expendables: Postradatelní 2', 'Expendables 2', 4, 'Stará známá banda Postradatelných Barney Ross (Sylvester Stallone), Lee Christmas (Jason Statham), Yin Yang (Jet Li), Gunner Jensen (Dolph Lundgren), Toll Road (Randy Couture) a Hale Caesar (Terry Crews) se dají opět dohromady poté, co je tajný agent CIA Church (Bruce Willis) povolá, aby jim zadal zdánlivě jednoduchou zakázku. K partičce se přidávají i noví členové Billy the Kid (Liam Hemsworth) a Maggie (Yu Nan) jako první žena v týmu Postradatelných. Ačkoliv úkol vypadá velice jednoduše, samotná akce se příliš nevydaří a jeden člen Postradatelných během ní dokonce zemře a to dost nepěknou smrtí. Jeho parťáci se ho rozhodnou pomstít přímo na místě, tedy na nepřátelském území, kde mají pouze mizivou šanci na úspěch. Postradatelným se podaří rozpoutat peklo na zemi, na které se mimochodem válí i pět tun čistého plutonia, které by naši planetu hravě vychýlilo z osy. Tuhle třaskavou směs musí zajistit dřív, než bude pozdě. To je ovšem brnkačka ve srovnání se šťavnatou pomstou za kamarádovu smrt.', 26, null, '1036', '2012', 1, 1, 0, 'http://www.csfd.cz/film/287500-expendables-postradatelni-2/', 'http://www.imdb.com/title/tt1764651/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1141, 'Amazing Spider-Man', 'Amazing Spider-Man', 4, 'The Amazing Spider-Man je příběhem Petera Parkera (Garfield), mezi svými vrstevníky nepříliš oblíbeného středoškoláka, kterého v dětství opustili rodiče a jeho výchovy se ujali strýček Ben (Sheen) a tetička May (Field). Jako většina dospívajících se Peter snaží zjistit, kým vlastně je a proč se z něj stal člověk, kterým je. Současně má plnou hlavu starostí se svou první středoškolskou láskou Gwen Stacyovou (Stone) a společně proplouvají úskalími lásky, závazků a tajemství. Když Peter objeví tajemný kufřík, který náležel jeho otci, pustí se do pátrání po důvodech zmizení svých rodičů – které ho zavede přímo do společnosti Oscorp a do laboratoře dr. Curta Connorse (Ifans), bývalého otcova kolegy. Zatímco je Spider-Manovi předurčeno, aby se střetl s Connorsovým alter-egem Lizardem, je Peter nucen učinit zásadní rozhodnutí o způsobu využití svých schopností, která navždy změní jeho život a definují povahu jeho cesty k hrdinství.', 26, null, '131', '2012', 1, 1, 0, 'http://www.csfd.cz/film/230573-amazing-spider-man/', 'http://www.imdb.com/title/tt0948470/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1142, 'Avengers', 'Avengers', 4, 'Marvel Studios uvádí super hrdinský tým všech dob Avengers, ve kterém se přestaví ikoničtí super hrdinové – Iron Man, Neuvěřitelný Hulk, Thor, Captain America, Hawkeye a Black Widow. Když se objeví nečekaný nepřítel, který ohrožuje světovou bezpečnost, Nick Fury, ředitel mezinárodní mírové agentury, známé také jako S.H.I.E.L.D., zjistí, že potřebuje tým, aby odvrátil světovou katastrofu. Začíná provádět nábor po celém světě.', 26, null, '143', '2012', 1, 1, 0, 'http://www.csfd.cz/film/241997-avengers/', 'http://www.imdb.com/title/tt0848228/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1143, 'Nespoutaný Django', 'Django Unchained', 11, 'Kultovní režisér Quentin Tarantino natočil jižanské drama z období před vypuknutím Občanské války, jehož hlavním hrdinou je otrok Django. Ten má šanci získat díky nájemnému lovci lidí dr. Schultzovi svobodu, pokud se mu povede dopadnout vraždící bratry Brittlovi. Djangovi se tak zároveň naskytne příležitost najít ženu, kterou musel před lety nedobrovolně opustit. Ani jeden z hlavních hrdinů však netuší, jak blízko jsou nejnebezpečnějšímu dobrodružství svého života.', 26, null, '165', '2012', 1, 1, 0, 'http://www.csfd.cz/film/294824-nespoutany-django/', 'http://www.imdb.com/title/tt1853728/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1144, 'Nevědomí', 'Oblivion', 3, 'Jacka (Tom Cruise), kterého na Zemi baví především hledání artefaktů zanikající lidské civilizace, vyvedou z každodenní rutiny dvě události. Tou první je nouzové přistání vesmírné lodi, jejíž jediná členka posádky (Olga Kurylenko) tvrdí, že ji s Jackem pojí společná minulost, aniž pro to existuje racionální vysvětlení. Následně je Jack zajat ilegální armádou operující pod zemským povrchem, jejíž charismatický vůdce Beech (Morgan Freeman) toho o něm ví víc než on sám. Pod vlivem těchto událostí se Jack rozhodne podívat na svět kolem sebe jinýma očima a z toho, co uvidí, mu není dvakrát dobře…', 26, null, '124', '2013', 1, 1, 0, 'http://www.csfd.cz/film/282846-nevedomi/', 'http://www.imdb.com/title/tt1483013/');
INSERT INTO MOVIE (ID, CZECH_NAME, ORIG_NAME, GENRE, DESCRIPTION, COUNTRY, IMAGE, LENGTH, "YEAR", INS_USERS, MODIF_USERS, DELETED, CSFD, IMDB) VALUES (1145, 'Babovřesky', 'Babovřesky', 2, 'Letní komedie Zdeňka Trošky je úsměvnou komedií ze života současné jihočeské vesnice Babovřesky, která s nadhledem a komediální nadsázkou, tak trochu v duchu Slunce, seno, sleduje kupící se nedorozumění a souhry náhod, které pořádně zamotají hlavu její obyvatelům. V příběhu nechybí láska, žárlivost, závist, intriky a zejména vtipné okamžiky, v nichž se někteří i najdou. Slovy režiséra: "Je to prostě film tak ''trošku'' ze života." Po smrti stoleté Trudy zůstanou na konci vesnice lukrativní pozemky se starým domem. To se hodí starostovi a jeho příbuznému, kteří mají na pozemky zálusk a chtějí zde vybudovat autobazar. Jenže pozůstalí Hálovi se synem Adamem, se rozhodnou, že se jim vila líbí a opraví ji, i když v ní prý straší. Podtitul "z dopisu venkovské drbny" naznačuje, že na scénu vstoupí i místní zbožné ženy v čele s "univerzální" klepnou Horáčkovou, která o všem ví, od všeho má klíče, všude byla dvakrát... a uzurpuje si právo do všeho zasahovat. A právě do této vesničky přichází nový farář. Zbožné ženy čekají váženého důstojného pána, ale z auta vyskočí mladý, sportovně vyhlížející asi třicetiletý mladík s dvojsmyslným jménem. Netuší, co tu na něj čeká, co všechno bude muset řešit a právě díky tomu se odehrává v Babovřeskách množství humorných situací.', 27, null, '133', '2013', 1, 1, 0, 'http://www.csfd.cz/film/316428-babovresky/', 'http://www.imdb.com/title/tt2719096/');
